import TextField from "../../common/TextField/withLabel";
import React from "react";
import {useEffect} from "react";
import {useSelector, useDispatch} from "react-redux";
import {personalInfoSelector} from "./selector";
import {updatePersonalInfo} from "./action";

export default (props) => {
    const {errors} = props;
    const { data } = useSelector(personalInfoSelector);
    const dispatch = useDispatch();
    const [form, setForm] = React.useState(data);
    useEffect(() => {
        dispatch(updatePersonalInfo(form));
    }, [form]);
    const updateReferenceValue = (target, index) => {
        const name = target.id.split('-')[0];
        const newReference = [...data.references];
        const toUpdate = newReference.map((ref, i) => {
            if (i == index) {
                return {
                    ...ref,
                    [name]: target.value
                }
            }
            return ref;
        });
        setForm({
            ...data,
            references: toUpdate
        });
    };
    const removereferences = (index) => {
        const newReferenses = [...data.references];
        newReferenses.splice(index, 1);
        setForm({
            ...data,
            references: newReferenses
        });
    };
    const addNewRow = () => {
        setForm({
            ...data,
            references: [
                ...data.references,
                {
                    firstName: '',
                    lastName: '',
                    phoneNumber: ''
                }
            ]
        });
    };
    return (
        <div className="card-body">
            <div className="col-lg-8 col-md-8">
                {
                    data.references.map((reference, index) => (
                        <div className="row" id="direferences" key={index}>
                            <div className="row">
                                <TextField name={`firstName-${index}`} label={'First Name'}
                                           placeholder={"First Name"} noLabel={false}
                                           divClass={"col-lg-3 col-md-3"}
                                           value={reference.firstName} updateValue={(target) => updateReferenceValue(target, index)} errors={errors[index] && errors[index]['firstName']}/>
                                <TextField name={`lastName-${index}`} label={'Last Name'}
                                           placeholder={"Last Name"} noLabel={false}
                                           divClass={"col-lg-3 col-md-3"}
                                           value={reference.lastName} updateValue={(target) => updateReferenceValue(target, index)} errors={errors[index] && errors[index]['lastName']}/>
                                <TextField name={`phoneNumber-${index}`} label={'Phone Number'}
                                           placeholder={"Phone Number"} noLabel={false}
                                           divClass={"col-lg-3 col-md-3"}
                                           value={reference.phoneNumber} updateValue={(target) => updateReferenceValue(target, index)} errors={errors[index] && errors[index]['phoneNumber']}/>
                                <div className="col-sm deleteIconMargin">
                                    <a onClick={() => removereferences(index)}><i
                                        className="fe fe-trash-2"></i></a>
                                </div>
                            </div>
                        </div>
                    ))
                }
                <div className="row">
                    <div className="col-lg-6 col-md-3">
                        <a id="btnreferences">
                            <div className="row">
                                <div className="col-lg-1 col-md-3">
                                    <i className="icon-2x icon-plus" data-bs-toggle="tooltip" title="" data-bs-original-title="icon-plus"
                                       aria-label="Add References" style={{"font-size": "20px"}} onClick={addNewRow}></i>
                                </div>
                                <div className="col-lg-6 col-md-3" onClick={addNewRow}>
                                    Add New
                                </div>
                            </div>
                        </a>
                    </div>
                </div>
            </div>
        </div>
    )
}
