import { useSelector, useDispatch } from "react-redux";
import {resumeBuilderSelector} from "./selector";
import {updateCategory} from "./action";
import DropDown from "../../common/TextField/dropDown";
import React, {useEffect} from "react";

export default (props) => {
    const dispatch = useDispatch();
    const {categories, masterDatas} = useSelector(resumeBuilderSelector);
    const updateCategories = (target) => {
        const value = target.value;
        const action = target.action || 'add';
        dispatch(updateCategory(value, action));
    };
    return (
        <div className="card">
            <div className="col-lg-12 col-md-12">
                <div className="row">
                    <div className="card-header">
                        <div className="col-lg-4 col-md-4">
                            <h4>Positions*</h4>
                        </div>
                    </div>
                </div>
            </div>
            <div className="card-body">
                <div className="row">
                    <div className="col-lg-12 col-md-12">
                        <div className="form-group">
                            <DropDown name={'categories'} label={'Please select the positions*'} id={'categories'}
                                      options={masterDatas.jobCategories} multiple={true} name={'category'} value={categories}
                                      updateValue={updateCategories} isObject={true} errors={props.errors ? props.errors['categories'] : []}/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
