import { setResumeBuilderData, setResumeBuilderError, setNoResume, setWorkHistory,
    setEducation, setCertifications, addPrimaryCertification, removePrimaryCertification,
    setSpecialities, setEducationLevel, addPrimaryEmrEhr, removePrimaryEmrEhr, setEmrEhr,
    setResumeFile, addCategory, removeCategory, setMasterDatas } from './slice';
import {setLoading} from "../../common/Loader/slice";
import requestUtil from "../../../helpers/requestUtil";
import {nextTab} from "../action";

export function updateEducationLevel(level) {
    return (dispatch) => {
        dispatch(setEducationLevel(level));
    }
}
export function updateNoResume(data) {
  return (dispatch) => {
    dispatch(setNoResume(data));
  };
}
export function updateWorkHistory(workHistory) {
  return (dispatch) => {
    dispatch(setWorkHistory(workHistory));
  };
}
export function updateEducation(education) {
  return (dispatch) => {
    dispatch(setEducation(education));
  };
}
export function updateSpeciality(speciality) {
  return (dispatch) => {
    dispatch(setSpecialities(speciality));
  };
}
export function updatePrimaryCertification(cert, action) {
  return (dispatch) => {
      switch (action) {
          case 'add':
              dispatch(addPrimaryCertification(cert));
              break;
          case 'remove':
              dispatch(removePrimaryCertification(cert));
              break;
          default:
              break;
      }
  };
}

export function updatePrimaryEmrEhr(emrEhr, action) {
  return (dispatch) => {
      switch (action) {
          case 'add':
              dispatch(addPrimaryEmrEhr(emrEhr));
              break;
          case 'remove':
              dispatch(removePrimaryEmrEhr(emrEhr));
              break;
          default:
              break;
      }
  };
}
export function updateCategory(category, action) {
    return (dispatch) => {
        switch (action) {
            case 'add':
                dispatch(addCategory(category));
                break;
            case 'remove':
                dispatch(removeCategory(category));
                break;
            default:
                break;
        }
    };
}
export function updateEmrEhr(emrEhr) {
  return (dispatch) => {
    dispatch(setEmrEhr(emrEhr));
  };
}
export function updateCertification(certification) {
  return (dispatch) => {
    dispatch(setCertifications(certification));
  };
}

export function setResumeBuilder(data) {
    return async dispatch => {
        dispatch(setResumeBuilderData(data));
    };
}
export function getCurrentResumeBuilder({url, userId}) {
    return async dispatch => {
        dispatch(setLoading(true));
        try {
            const response = await requestUtil(`${url}/${userId}`, 'GET');
            dispatch(setResumeBuilderData(response));
        } catch (error) {
            dispatch(setResumeBuilderError(true))
        }
        dispatch(setLoading(false));
    };
}

export function saveResumeBuilder({url, userId, gotoNextTab=true}) {
    return async (dispatch, getState) => {
        dispatch(setLoading(true));
        const { data } = getState().resumeBuilder;
        try {
            //await requestUtil(`${url}/${userId}`, 'POST', data);
            gotoNextTab && dispatch(nextTab());
        } catch (error) {
            dispatch(setResumeBuilderError(true))
        }
        dispatch(setLoading(false));
    };
}

export function updateResumeFile(file) {
    return (dispatch) => {
        dispatch(setResumeFile(file));
    };
}

export function loadMasterDatas(url) {
    return async dispatch => {
        dispatch(setLoading(true));
        try {
            const response = await requestUtil(`${url}/Dropdown/GetLevelOfEducations`, 'GET');
            dispatch(setMasterDatas({
                educationLevel: response
            }));
        } catch (error) {
            dispatch(setResumeBuilderError(true))
        }
        dispatch(setLoading(false));
    };
}

export function loadJobCategories(url, jobId) {
    return async dispatch => {
        dispatch(setLoading(true));
        try {
            const response = await requestUtil(`${url}/Dropdown/getjobcategories/${jobId}`, 'GET');
            dispatch(setMasterDatas({
                jobCategories: response
            }));
        } catch (error) {
            dispatch(setResumeBuilderError(true))
        }
        dispatch(setLoading(false));
    };
}
