import {useState} from "react";
import Modal from "./modal";
import TravelDistanceForm from "../ProfileBuilder/Preference/travelDistanceForm";

export default (props) => {
    const [showModal, setShowModal] = useState(false);
    const editTravelDistance = (event) => {
        event.preventDefault();
        setShowModal(!showModal);
        if (showModal) {
            props.cancel('preference');
        }
    };
    const saveTravelDistance = (event) => {
        event.preventDefault();
        if (props.saveContinue('preference', false)) {
            setShowModal(!showModal);
        }
    };
    return (
        <div className="col-md-12 col-xl-12">
            <div className="card">
                {
                    showModal &&
                    <Modal label={"Travel Distance"} showHide={editTravelDistance} saveFunction={saveTravelDistance}>
                        <TravelDistanceForm errors={props.errors}/>
                    </Modal>
                }
                <div className="col-lg-12 col-md-12">
                    <div className="row">
                        <div className="card-header">
                            <div className="col-lg-4 col-md-4">
                                <h3>Travel Distance</h3>
                            </div>
                            <div className="col-lg-2 col-md-2">
                                <a id="EditDistance" href="#" onClick={editTravelDistance}>
                                    <i className="fe fe-edit" title="Edit Travel Distance"></i>
                                </a>
                            </div>
                        </div>
                        <div className="card-body">
                            <div className="form-group">
                                <div className="col-lg-12 col-md-12">
                                    <label htmlFor="exampleInputname">{props.travelDistance.name}</label>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
