import React, {useEffect} from 'react';
import { useDispatch, useSelector } from "react-redux";
import useConfiguration  from '../../hooks/useConfiguration';
import useSessionStorage from "../../hooks/useSessionStorage";
import Header from "../common/Header";
import Sidebar from "../common/Sidebar";
import Footer from "../common/Footer";
import PageHeader from "./pageHeader";
import './profilebuilder.css';
import PersonalInfo from "./PersonalInfo";
import { getCurrentPersonalInfo, savePersonalInfo } from './PersonalInfo/action';
import { saveResumeBuilder, getCurrentResumeBuilder } from "./ResumeBuilder/action";
import {savePreference} from "./Preference/action";
import './index.css';
import Loader from "../common/Loader";
import {profileBuilderSelector} from './selector';
import { previousTab } from "./action";
import ResumeBuilder from "./ResumeBuilder";
import Preference from "./Preference";
import Summary from "../Summary";

export default () => {
    const dispatch = useDispatch();
    const config = useConfiguration();
    const storage = useSessionStorage();
    const {activeTab} = useSelector(profileBuilderSelector);
    const login = storage.getItem("login");
    useEffect(() => {
        if(config.baseUrl) {
            dispatch(getCurrentPersonalInfo({
                url: `${config.baseUrl}/Candidates/getpersonalinfo`, userId: login.appUserId
            }));
            dispatch(getCurrentResumeBuilder({
                url: `${config.baseUrl}/CandidatesRegisteration/`, userId: login.appUserId
            }));
        }
    }, [config]);

    const tabStyle = (tabName) => {
        return activeTab === tabName ? 'nav-item active' : 'nav-item';
    };

    const saveContinue = () => {
        if(activeTab === 'personalInfo') {
            dispatch(savePersonalInfo({
                url: `${config.baseUrl}/Candidates/savepersonalinfo/`, userId: login.appUserId
            }));
        } else if(activeTab === 'resumeBuilder') {
            dispatch(saveResumeBuilder({
                url: `${config.baseUrl}/CandidatesRegisteration/`, userId: login.appUserId
            }));
        } else if(activeTab === 'preference') {
            dispatch(savePreference({
                url: `${config.baseUrl}/CandidatesRegisteration/`, userId: login.appUserId
            }));
        }
    };

    const gotoPrevious = () => {
        dispatch(previousTab());
    };

    const previousDisabled = activeTab === 'personalInfo';
    return (
        <div className="page">
            <div className="page-main">
                <Loader />
                <Header />
                <Sidebar />
                <div className={"main-content app-content mt-0 main-background"}>
                    <div className="side-app">
                        <div className="main-container container-fluid">
                            <PageHeader previous={"Dashboard"} current={"Profile Builder"}/>
                            { activeTab !== 'summary' &&
                            <div className="row">
                                <div className="col-12">
                                    <div className="card">
                                        <div className="card-header border-bottom-0">
                                            <h3 className="card-title">Profile Builder</h3>
                                        </div>
                                        <div className="card-body">
                                            <div id="smartwizard-3" className={"sw-main sw-theme-dots"}>
                                                <ul className={"nav nav-tabs step-anchor"}>
                                                    <li className={tabStyle('personalInfo')}><a className={'nav-link'} href="#" onFocus={(e) => e.preventDefault()}>Personal Info</a></li>
                                                    <li className={tabStyle('resumeBuilder')}><a className={'nav-link'} href="#" onFocus={(e)=> e.preventDefault()}>Resume Builder</a></li>
                                                    <li className={tabStyle('preference')}><a className={'nav-link'} href="#" onFocus={e => e.preventDefault()}>Preference</a></li>
                                                </ul>
                                                <div className={"tab-content sw-container"}>
                                                    {
                                                        activeTab === 'personalInfo' &&
                                                        <div id={"step-personalInfo"}>
                                                            <PersonalInfo />
                                                        </div>
                                                    }
                                                    {
                                                        activeTab === 'resumeBuilder' &&
                                                        <div id={"step-resumeBuilder"}>
                                                           <ResumeBuilder />
                                                        </div>
                                                    }
                                                    {
                                                        activeTab === 'preference' &&
                                                        <div id={"step-preference"}>
                                                            <Preference />
                                                        </div>
                                                    }
                                                    <div className="btn-toolbar sw-toolbar sw-toolbar-bottom justify-content-end">
                                                        <div className="btn-group me-2 sw-btn-group" role="group">
                                                            <button className="btn btn-primary sw-btn-next" type="button" onClick={saveContinue}>Save and Continue</button>
                                                        </div>
                                                        <div className="btn-group mr-2 sw-btn-group-extra" role="group">
                                                            <button className="btn btn-danger">Cancel</button>
                                                        </div>
                                                    </div>
                                                    <div className="btn-toolbar sw-toolbar sw-toolbar-bottom justify-content-left">
                                                        <div className="btn-group me-2 sw-btn-group prev-margin" role="group">
                                                            <button className={`btn btn-danger sw-btn-prev ${previousDisabled? 'disabled': ''}`} type="button" onClick={gotoPrevious}>Previous</button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>}
                            {
                                activeTab === 'summary' && <Summary />
                            }
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    )
}
