import {useState} from "react";
import Modal from "./modal";
import ShiftsForm from "../ProfileBuilder/Preference/shiftsForm";

export default (props) => {
    const [showModal, setShowModal] = useState(false);
    const editShifts = (event) => {
        event.preventDefault();
        setShowModal(!showModal);
        if (showModal) {
            props.cancel('preference');
        }
    };
    const saveShifts = (event) => {
        event.preventDefault();
        if (props.saveContinue('preference', false)) {
            setShowModal(!showModal);
        }
    };
    return (
        <div className="col-md-12 col-xl-12">
            <div className="card">
                {
                    showModal && <Modal
                        showHide={editShifts}
                        saveFunction={saveShifts}
                        label={"Preferred Shifts"}>
                        <ShiftsForm errors={props.errors}/>
                    </Modal>
                }
                <div className="col-lg-12 col-md-12">
                    <div className="row">
                        <div className="card-header">
                            <div className="col-lg-4 col-md-4">
                                <h3>Preferred Shifts</h3>
                            </div>
                            <div className="col-lg-2 col-md-2">
                                <a id="EditShifts" href="#" onClick={editShifts}>
                                    <i className="fe fe-edit" title="Edit Preferred Shifts"></i>
                                </a>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card-body">
                    <div className="form-group">
                        {
                            props.preferredShifts.map(shift => (
                                <div className="col-lg-12 col-md-12">
                                    <label htmlFor="exampleInputname">{shift.name}</label>
                                </div>
                            ))
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}
