import TextField from "../../common/TextField/withLabel";
import React, {useEffect} from "react";
import {personalInfoSelector} from "./selector";
import {updatePersonalInfo} from "./action";
import {useDispatch, useSelector} from "react-redux";

export default () => {
    const { data } = useSelector(personalInfoSelector);
    const dispatch = useDispatch();
    const [form, setForm] = React.useState(data);
    useEffect(() => {
        dispatch(updatePersonalInfo(form));
    }, [form]);
    const updateEmergencyForm = (target) => {
        setForm({
            ...form,
            emergencyContact: {
                ...form.emergencyContact,
                [target.name]: target.value
            }
        });
    };
    return (
        <div className="card-body">
            <div className="row">
                <TextField name={'firstName'} label={'First Name*'} placeholder={"First Name"}
                           noLabel={true} updateValue={updateEmergencyForm} value={data.emergencyContact.firstName}/>
                <TextField name={'lastName'} label={'Last Name*'} placeholder={"Last Name"}
                           noLabel={true} updateValue={updateEmergencyForm} value={data.emergencyContact.lastName}/>
            </div>
            <div className="row">
                <TextField name={'phoneNumber'} placeholder={"Phone Number"} noLabel={true} updateValue={updateEmergencyForm} value={data.emergencyContact.phoneNumber}/>
                <TextField name={'relationship'} placeholder={"Relationship"} noLabel={true} updateValue={updateEmergencyForm} value={data.emergencyContact.relationship}/>
            </div>
        </div>
    )
}
