import {createSlice} from "@reduxjs/toolkit";
const initialState = {
  isError: false,
  isSuccess: false,
  errorMessage: '',
  data: {}
};

export const loginSlice = createSlice({
    name: 'login'
  , initialState,

    reducers: {
        loginRequest: (state) => {
        state.isError = false;
        state.isSuccess = false;
        state.errorMessage = '';
        data: {}
      },
      loginSuccess: (state, action) => {
        state.isError = false;
        state.isSuccess = true;
        state.errorMessage = '';
        state.data = action.payload;
      },
      loginFailure: (state, action) => {
        state.isError = true;
        state.isSuccess = false;
        state.errorMessage = action.payload;
      },
    }
});

export const {
  loginRequest,
  loginSuccess,
  loginFailure
} = loginSlice.actions;

export default loginSlice.reducer;
