import React, {useEffect} from "react";
import { useSelector } from "react-redux";
import { dashboardSelector } from "./selector";
import { loadDashboard }  from "./action";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import Loader from "../common/Loader";
import Header from "../common/Header";
import Sidebar from "../common/Sidebar";
import Footer from "../common/Footer";
import Card from "./card";
import useConfiguration from "../../hooks/useConfiguration";
import useSessionStorage from "../../hooks/useSessionStorage";

export default () => {
  const dispatch = useDispatch();
  const config = useConfiguration();
  const storage = useSessionStorage();
  const navigate = useNavigate();
  const login = storage.getItem("login");
  const { data } = useSelector(dashboardSelector);
  const profileSubmitted = data.profileFlag && data.profileFlag.isProfileSubmitted;
  useEffect(() => {
    document.title = "Overture Dashboard";
    if(config.baseUrl) {
      if(!login) {
        navigate("/");
      } else {
        dispatch(loadDashboard(config.baseUrl, login.candidateId));
      }
    }
  }, [config]);
  const editProfile = (event) => {
    event.preventDefault();
    navigate("/profile");
  };
  return (
      <div className="page">
        <Loader />
        <Header />
        <Sidebar />
        <div className="page-main">
          <div className="main-content app-content mt-0 main-background">
            <div className="side-app">
              <div className="main-container container-fluid">
                <div className="page-header">
                  <h1 className="page-title">Candidate Dashboard</h1>
                  <div>
                    <ol className="breadcrumb">
                      <li className="breadcrumb-item"><a href="javascript:void(0)">Dashboard</a></li>
                    </ol>
                  </div>
                </div>
                <div className="row">
                  <div className="col-sm-6 col-md-6 col-lg-6 col-xl-3">
                    <a href="#" onClick={editProfile}>
                      <Card title={"Profile Builder"} value={data.percentageOfCompletion}
                            valueLabel={profileSubmitted ? "Edit Profile" : "Complete Profile"} pathColor={"#4ecc48"}
                            bgClass={"bg-green"} valueSuffix={"%"}>
                        <a data-bs-toggle="slide" href="#">
                          <div className="chart-circle-value-3 text-secondary fs-30">
                            <i className="icon icon-user-follow"></i>
                          </div>
                        </a>
                      </Card>
                    </a>
                  </div>
                </div>
                <div className="row">
                  <div className="col-sm-6 col-md-6 col-lg-6 col-xl-3">
                    <Card title={"Schedule"} value={60} valueLabel={"My Schedule"}
                          bgClass={"bg-secondary"} pathColor={"#05c3fb"} valueSuffix={"%"}>
                      <a data-bs-toggle="slide" href="#" onClick={editProfile}>
                        <div className="chart-circle-value-3 text-secondary fs-30">
                          <i className="icon icon-user-follow"></i>
                        </div>
                      </a>
                    </Card>
                  </div>
                  <div className="col-sm-6 col-md-6 col-lg-6 col-xl-3">
                    <Card title={"Opportunities"} value={10} pathColor={"#09ad95 "}
                          bgClass={"bg-success"} valueLabel={"Opportunities"} valueSuffix={"%"}>
                      <div className="chart-circle-value-3 text-success fs-20">
                        <i className="icon icon-briefcase"></i>
                      </div>
                    </Card>
                  </div>
                  <div className="col-sm-6 col-md-6 col-lg-6 col-xl-3">
                    <Card smallTitle={"Employers"} title={"Authorized"} value={10} pathColor={"#f7b731"}
                          bgClass={"bg-warning"} valueLabel={"No of Employers"}>
                      <div className="chart-circle-value-3 text-warning fs-20">
                        <i className="fa fa-building-o"/>
                      </div>
                    </Card>
                  </div>
                  <div className="col-sm-10 col-md-6 col-lg-6 col-xl-3.1">
                    <Card smallTitle={"Payment"} title={"Status"} value={20} pathColor={"#e82646"}
                          bgClass={"bg-danger"} valueLabel={"No of Payment Received"}>
                      <div className="chart-circle-value-3 text-danger fs-20">
                        <i className="mdi mdi-currency-usd"/>
                      </div>
                    </Card>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
  );
};
