import { useSelector, useDispatch } from "react-redux";
import {personalInfoSelector} from "../ProfileBuilder/PersonalInfo/selector";
import {resumeBuilderSelector} from "../ProfileBuilder/ResumeBuilder/selector";
import {preferenceSelector} from '../ProfileBuilder/Preference/selector';
import Registration from './registration';
import Emergency from "./emergency";
import References from "./references";
import ResumeUpload from "./resumeUpload";
import Categories from "./categories";
import Certifications from "./certifications";
import EducationLevel from "./educationLevel";
import Specialities from "./specialities";
import Emrehr from "./emrehr";
import Shifts from "./shifts";
import TravelDistance from "./travelDistance";
import PrefferedWorkdays from "./prefferedWorkdays";
import Holiday from "./holiday";
import Covid from "./covid";
import {resetErrorStatus, savePersonalInfo} from "../ProfileBuilder/PersonalInfo/action";
import {saveResumeBuilder} from "../ProfileBuilder/ResumeBuilder/action";
import {savePreference} from "../ProfileBuilder/Preference/action";
import useConfiguration from "../../hooks/useConfiguration";
import useSessionStorage from "../../hooks/useSessionStorage";
import {profileBuilderSelector} from "../ProfileBuilder/selector";
import { gotoNextTab } from "../ProfileBuilder/slice";
import React from "react";
import './index.css';
export default () => {
    const {data} = useSelector(personalInfoSelector);
    const { workHistory, data: resumeData, education, categories, certifications, educationLevel, specialities, emrehr, masterDatas: resumeMasterDatas } = useSelector(resumeBuilderSelector);
    const {preferredShifts, travelDistance, preferredWorkDays, holidayPreference, vaccination, masterDatas} = useSelector(preferenceSelector);
    const config = useConfiguration();
    const storage = useSessionStorage();
    const login = storage.getItem('login');
    const dispatch = useDispatch();
    const { error, errorMessage} = useSelector(profileBuilderSelector);
    const saveContinue = (activeTab) => {
        dispatch(resetErrorStatus())
        if(activeTab === 'personalInfo') {
            dispatch(savePersonalInfo({
                url: `${config.baseUrl}/Candidates/savepersonalinfo/`, userId: login.appUserId, gotoNextTab: false
            }));
        } else if(activeTab === 'resumeBuilder') {
            dispatch(saveResumeBuilder({
                url: `${config.baseUrl}`, userId: login.appUserId, gotoNextTab: false
            }));
        } else if(activeTab === 'preference') {
            dispatch(savePreference({
                url: `${config.baseUrl}`, userId: login.appUserId, gotoNextTab: false
            }));
        }
    };

    return(
            <div className={"row"}>
                <div className={"col-12"}>
                    <div className={"card"}>
                        <div className="card-header border-bottom-0">
                            <h3 className="card-title">Profile Builder</h3>
                        </div>
                        {
                            error &&
                            <>
                                <div className="toast wrap-input100 align-items-center text-white bg-danger border-0 show" role="alert" aria-live="assertive" aria-atomic="true" data-bs-autohide="false">
                                    <div className="d-flex col-6">
                                        <div className="toast-body">
                                            {errorMessage || 'Save error! Please try again.'}
                                        </div>
                                    </div>
                                </div>
                                <div>&nbsp;</div>
                            </>
                        }
                        <div className={"card-body"}>
                            <div id={"ReviewPage"}>
                                <Registration {...data} saveContinue={saveContinue}/>
                                <Emergency {...data} saveContinue={saveContinue}/>
                                <References {...data} saveContinue={saveContinue}/>
                                <ResumeUpload {...resumeData} workHistory={workHistory} education={education} saveContinue={saveContinue}/>
                                <Categories categories={categories} saveContinue={saveContinue}/>
                                <Certifications {...certifications} saveContinue={saveContinue}/>
                                <EducationLevel educationLevel={educationLevel} masterEducationLevel={resumeMasterDatas.educationLevel} saveContinue={saveContinue}/>
                                <Specialities specialities={specialities} saveContinue={saveContinue}/>
                                <Emrehr {...emrehr} saveContinue={saveContinue} />
                                <Shifts preferredShifts={preferredShifts} saveContinue={saveContinue} />
                                <TravelDistance travelDistance={travelDistance} saveContinue={saveContinue}/>
                                <PrefferedWorkdays preferredWorkDays={preferredWorkDays} masterWorkdays={masterDatas.preferredWorkDays} saveContinue={saveContinue} />
                                <Holiday {...holidayPreference} masterHolidays={masterDatas.holidayPreferenceRaw} saveContinue={saveContinue}/>
                                <Covid vaccination={masterDatas.covidVaccine.find(vac => vac.id===vaccination).name} masterVaccine={masterDatas.covidVaccine} saveContinue={saveContinue}/>
                            </div>
                        </div>
                        <div className="card-footer text-end">
                            <div className="col-lg-10 col-md-12">
                                <a href="ProfileBuilder" className="btn btn-light float-left">Back</a>
                                <a href="#" className="btn btn-primary"> Save </a> &nbsp;
                                <a href="#" className="btn btn-primary" onClick={(event)=> {
                                    event.preventDefault();
                                    dispatch(gotoNextTab())
                                }}>Submit Profile</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
    )
}
