import TextField from "../../common/TextField/withLabel";
import React, {useEffect} from "react";
import {useSelector, useDispatch} from "react-redux";
import {personalInfoSelector} from "./selector";
import {getCityState, updatePersonalInfo, getSpokenLanguages, updateLanguageList} from "./action";
import useConfiguration from "../../../hooks/useConfiguration";
import DropDown from "../../common/TextField/dropDown";

export default (props) => {
    const {errors} = props;
    const {data, masterData} = useSelector(personalInfoSelector);
    const dispatch = useDispatch();
    const config = useConfiguration();
    const [form, setForm] = React.useState(data);
    useEffect(() => {
        dispatch(updatePersonalInfo(form));
    }, [form]);
    const updateFormValue = (target) => {
        setForm({
            ...data,
            [target.name]: target.value
        });
        if (target.name === 'zipcode' && target.value.length >= 5) {
            zipCodeUpdated(target.value);
        }
    };
    const updateLangValue = (target) => {
        const value = target.value;
        const action = target.action || 'add';
        dispatch(updateLanguageList(value, action));
    };
    const zipCodeUpdated = (value) => {
        dispatch(getCityState(`${config.baseUrl}/Zipcodes/getzipcodeinfo/${value}`));
    }


    return (
        <div className="card-body">
            <div className="row">
                <TextField name={'firstName'} label={'First Name*'} placeholder={"First Name"}
                           updateValue={updateFormValue} value={data.firstName} errors={errors['firstName']}/>
                <TextField name={'lastName'} label={'Last Name*'} placeholder={"Last Name"}
                           updateValue={updateFormValue} value={data.lastName} errors={errors['lastName']}/>
            </div>
            <div className="row">
                <TextField name={'email'} label={'Email*'} placeholder={"Email"} updateValue={updateFormValue}
                           value={data.email} errors={errors['email']}/>
                <TextField label={'Street Address*'} name={'streetAddress'}
                           placeholder={"Street Address"} updateValue={updateFormValue} value={data.streetAddress}
                           errors={errors['streetAddress']}/>
            </div>
            <div className="row">
                <TextField label={'Zip Code*'} name={'zipcode'} placeholder={"Zip Code"} updateValue={updateFormValue}
                           value={data.zipcode} errors={errors['zipcode']}/>
                <TextField label={'City*'} name={'city'} placeholder={"City"} updateValue={updateFormValue}
                           value={data.city} errors={errors['city']}/>
            </div>
            <div className="row">
                <TextField label={'State*'} name={'stateName'} placeholder={"State"} updateValue={updateFormValue}
                           value={data.stateName} errors={errors['stateName']}/>
                <TextField label={'Cell Number*'} name={'cellPhoneNumber'}
                           placeholder={"Cell Number"} updateValue={updateFormValue}
                           value={data.cellPhoneNumber} errors={errors['cellPhoneNumber']}
                           format={"phone"}
                />
            </div>
            <div className="row">
                <TextField label={'Home Phone Number'} name={'homePhoneNumber'}
                           placeholder={"Home Phone Number"} updateValue={updateFormValue}
                           value={data.homePhoneNumber} errors={errors['homePhoneNumber']} format={"phone"}/>

                <TextField label={'Alternate Phone Number'} name={'alternatePhoneNumber'} format={"phone"}
                           placeholder={"Alternate Phone Number"} updateValue={updateFormValue}
                           value={data.alternatePhoneNumber} errors={errors['alternatePhoneNumber']}/>
            </div>
            <div className={"row"}>
                <DropDown name={"spokenLanguagesList"} label={'Spoken Languages*'}
                          placeholder={"Spoken Languages"} updateValue={updateLangValue}
                          value={data.spokenLanguagesList || []}
                          options={masterData.languages} showArrow={true} isObject={true} multiple={true}
                          errors={errors['spokenLanguagesList']}/>
            </div>
        </div>
    )
}
