import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { resumeBuilderSelector } from './selector';
import {updatePrimaryEmrEhr, updateEmrEhr, loadEmrehr} from './action';
import DropDown from "../../common/TextField/dropDown";
import useConfiguration from "../../../hooks/useConfiguration";

export default (props) => {
  const dispatch = useDispatch();
  const { emrehr, masterDatas } = useSelector(resumeBuilderSelector);
  const [data, setData] = useState(emrehr);
  const config = useConfiguration();
    useEffect(() => {
      dispatch(updateEmrEhr(data));
  }, [data])
    useEffect(() => {
        config.baseUrl && dispatch(loadEmrehr(config.baseUrl, 1));
    }, [config]);
    const updateData = (target) => {
        const value = target.value;
        const action = target.action || 'add';
        dispatch(updatePrimaryEmrEhr(value, action));
    };
    const updateOtherData = (target, index) => {
        const newEmrehr = [...emrehr.others];
        newEmrehr[index] = target.value;
        setData({
            ...emrehr,
            others: newEmrehr
        });
    };

    const removeRow = (index) => {
        const newEmrehr = [...emrehr.others];
        newEmrehr.splice(index, 1);
        setData({
            ...emrehr,
            others: newEmrehr
        });
    };

    const addRow = (event) => {
        event.preventDefault();
        const newEmrehr = [...emrehr.others];
        newEmrehr.push("");
        setData({
            ...emrehr,
            others: newEmrehr
        });
    };

    return (
        <div id="DivFor_EHREMR">
            <div id="largemodal_EHREMR_1">
                <div id="largemodal_EHREMR_2">
                    <div id="largemodal_EHREMR_3">
                        <div className="card">
                            <div className="col-lg-12 col-md-12">
                                <div className="row">
                                    <div className="card-header">
                                        <div className="col-lg-12 col-md-12">
                                            <h4>EMR/EHR System Experience</h4>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="card-body">
                                <DropDown name={'emrehr'} label={'Select all that apply*'} id={'emrehr'}
                                          options={masterDatas.emrehr} multiple={true} name={'primary'}
                                          updateValue={updateData} isObject={true}/>
                            </div>
                            <div className="card-body">
                                <div className="col-lg-12 col-md-12">
                                    <div className="row">
                                        <div className="col-lg-12 col-md-12">
                                            <h4>Other EMR/EHR System experience</h4>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div className="card-body">
                                <div className="col-lg-12 col-md-12">
                                    {
                                        emrehr.others.map((other, index) => (
                                            <div className="row">
                                                <div className="col-lg-12 col-md-12" id="divAddEMREHRSystem">
                                                    <div className="row" id="">
                                                        <div className="col-sm">
                                                            <input type="text" value={other} className="form-control" id="othercertifyname"
                                                                   placeholder="List EMR/EHR System" onChange={(event) => updateOtherData(event.target, index)}/>
                                                        </div>
                                                        <div className="col-sm">
                                                            <a href="#" onClick={(event)=> {
                                                                event.preventDefault();
                                                                removeRow(index)
                                                            }}><i className="fe fe-trash-2"></i></a>
                                                        </div>
                                                        <div className="row">
                                                            &nbsp;
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        ))
                                    }

                                    <div className="row">
                                        <div className="col-lg-3 col-md-3">
                                            <a id="btnAddEMREHRSystem" href="#" onClick={addRow}>
                                                <div className="row">
                                                    <div className="col-lg-1 col-md-3">
                                                        <i className="icon-2x icon-plus" data-bs-toggle="tooltip"
                                                           title="" data-bs-original-title="icon-plus"
                                                           aria-label="Add Certifications"></i>
                                                    </div>
                                                    <div className="col-lg-6 col-md-3">
                                                        Add New
                                                    </div>
                                                </div>
                                            </a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
