const {createSlice} = require('@reduxjs/toolkit');

const initialState = {
    data: {
        noResume: false,
        resumeFile: {
            name: '',
            content: null
        }
    },
    workHistory: [
        {
            employer: '',
            fromMonth: '',
            fromYear: '',
            toMonth: '',
            toYear: '',
        }
    ],
    education: [
        {
            education: '',
            fromMonth: '',
            fromYear: '',
            toMonth: '',
            toYear: '',
        }
    ],
    categories: {
      selected: [],
    },
    certifications: {
        primary: [],
        others: [""],
    },
    specialities: [{
       skill: '',
       years: '',
    }],
    emrehr:{
        primary: [],
        others: [""],
    },
    educationLevel: '',
    resumeBuilderError: false,
    masterDatas: {
        educationLevel: [],
        jobCategories: [],
        emrehr: [],
        specialities:[],
        certifications: [],
    }
}
export const resumeBuilderSlice = createSlice({
    name: 'resumeBuilder',
    initialState,
    reducers: {
        setEducationLevel: (state, action) => {
            state.educationLevel = action.payload;
        },
        setSpecialities: (state, action) => {
            state.specialities = action.payload;
        },
        addCategory: (state, action) => {
            const newCategory = [...state.categories.selected, action.payload];
            state.categories.selected = newCategory;
        },
        removeCategory: (state, action) => {
            const newCategory = state.categories.selected.filter(category => category !== action.payload);
            state.categories.selected = newCategory;
        },
        addPrimaryEmrEhr: (state, action) => {
            const newPrimary = [...state.emrehr.primary, action.payload];
            state.emrehr.primary = newPrimary;
        },
        removePrimaryEmrEhr: (state, action) => {
            const newPrimary = state.emrehr.primary.filter(item => item !== action.payload);
            state.emrehr.primary = newPrimary;
        },
        setEmrEhr: (state, action) => {
            state.emrehr = action.payload;
        },
        addPrimaryCertification: (state, action) => {
            const newPrimary = [...state.certifications.primary, action.payload];
            state.certifications.primary = newPrimary;
        },
        removePrimaryCertification: (state, action) => {
            const newPrimary = state.certifications.primary.filter(cert => cert.id !== action.payload.id);
            state.certifications.primary = newPrimary;
        },
        setCertifications: (state, action) => {
            state.certifications = action.payload;
        },
        setEducation: (state, action) => {
            state.education = action.payload;
        },
        setWorkHistory: (state, action) => {
            state.workHistory = action.payload;
            state.resumeBuilderError = false;
        },
        setResumeBuilderData: (state, action) => {
            state.data = action.payload;
            state.resumeBuilderError = false;
        },
        setNoResume: (state, action) => {
            state.data.noResume = action.payload;
            state.data.resumeFile = {
                name: '',
               content: null
            };
            state.resumeBuilderError = false;
        },
        setResumeFile: (state, action) => {
            state.data.resumeFile = action.payload;
            state.data.noResume = false;
            state.resumeBuilderError = false;
        },
        setResumeBuilderError: (state, action) => {
            state.resumeBuilderError = action.payload;
        },
        setMasterDatas: (state, action) => {
            if(action.payload.educationLevel) {
                state.masterDatas.educationLevel = action.payload.educationLevel;
            }
            if(action.payload.jobCategories) {
                state.masterDatas.jobCategories = action.payload.jobCategories;
            }
            if(action.payload.emrehr) {
                state.masterDatas.emrehr = action.payload.emrehr;
            }
            if(action.payload.certifications) {
                state.masterDatas.certifications = action.payload.certifications;
            }
            if(action.payload.specialities) {
                state.masterDatas.specialities = action.payload.specialities;
            }
        }
    },
});
export const { setResumeBuilderData, setResumeBuilderError, setWorkHistory, setNoResume,
    setEducation, setCertifications, addPrimaryCertification,removePrimaryCertification,
    setSpecialities, setEducationLevel, addPrimaryEmrEhr, removePrimaryEmrEhr, setEmrEhr,
    setResumeFile, addCategory, removeCategory, setMasterDatas } = resumeBuilderSlice.actions;
export default resumeBuilderSlice.reducer;
