import { useSelector, useDispatch } from "react-redux";
import {personalInfoSelector} from "../ProfileBuilder/PersonalInfo/selector";
import {resumeBuilderSelector} from "../ProfileBuilder/ResumeBuilder/selector";
import {preferenceSelector} from '../ProfileBuilder/Preference/selector';
import Registration from './registration';
import Emergency from "./emergency";
import References from "./references";
import ResumeUpload from "./resumeUpload";
import Categories from "./categories";
import Certifications from "./certifications";
import EducationLevel from "./educationLevel";
import Specialities from "./specialities";
import Emrehr from "./emrehr";
import Shifts from "./shifts";
import TravelDistance from "./travelDistance";
import PrefferedWorkdays from "./prefferedWorkdays";
import Holiday from "./holiday";
import Covid from "./covid";
import useConfiguration from "../../hooks/useConfiguration";
import useSessionStorage from "../../hooks/useSessionStorage";
import {profileBuilderSelector} from "../ProfileBuilder/selector";
import { gotoNextTab, gotoPreviousTab } from "../ProfileBuilder/slice";
import requestUtil from "../../helpers/requestUtil";
import React, {useEffect} from "react";
import './index.css';
export default (props) => {
    const {data} = useSelector(personalInfoSelector);
    const { workHistory, data: resumeData, educationList, categories, certifications, educationLevel, specialities, emrehr, masterDatas: resumeMasterDatas } = useSelector(resumeBuilderSelector);
    const {preferredShifts, travelDistance, preferredWorkDays, holidayPreference, vaccination, masterDatas} = useSelector(preferenceSelector);
    const config = useConfiguration();
    const storage = useSessionStorage();
    const login = storage.getItem('login');
    const dispatch = useDispatch();
    const { error, errorMessage} = useSelector(profileBuilderSelector);
    const { saveContinue } = props;
    useEffect(() => {
        window.scrollToTop && window.scrollToTop();
    }, []);
    const submitProfile = (event) => {
        event.preventDefault();
        requestUtil(`${config.baseUrl}/Candidates/savesubmitinfo`, 'POST', {
            candidateId: login.candidateId,
            appUserId: login.appUserId,
        }).then(res => {
            dispatch(gotoNextTab());
        }).catch(err => {
            console.log(err);
        });
    }
    return(
            <div className={"row"}>
                <div className={"col-12"}>
                    <div className={"card"}>
                        <div className="card-header border-bottom-0">
                            <h3 className="card-title">Profile Builder</h3>
                        </div>
                        {
                            error &&
                            <>
                                <div className="toast wrap-input100 align-items-center text-white bg-danger border-0 show" role="alert" aria-live="assertive" aria-atomic="true" data-bs-autohide="false">
                                    <div className="d-flex col-6">
                                        <div className="toast-body">
                                            {errorMessage || 'Save error! Please try again.'}
                                        </div>
                                    </div>
                                </div>
                                <div>&nbsp;</div>
                            </>
                        }
                        <div className={"card-body"}>
                            <div id={"ReviewPage"}>
                                <Registration {...data} saveContinue={saveContinue} errors={props.registrationErrors} />

                                <ResumeUpload {...resumeData} workHistory={workHistory}
                                              education={educationList} saveContinue={saveContinue}
                                              errors={props.resumeBuilderError}
                                              educationErrors={props.educationErrors}
                                              workHistoryErrors={props.workHistoryErrors} />
                                <Categories categories={categories} saveContinue={saveContinue} errors={props.resumeBuilderError}/>
                                <Certifications {...certifications} saveContinue={saveContinue} errors={props.resumeBuilderError}/>
                                <EducationLevel educationLevel={educationLevel} masterEducationLevel={resumeMasterDatas.educationLevel} saveContinue={saveContinue} errors={props.resumeBuilderError}/>
                                <Specialities specialities={specialities} masterDatas={resumeMasterDatas} saveContinue={saveContinue}/>
                                <Emrehr {...emrehr} saveContinue={saveContinue} errors={props.resumeBuilderError}/>
                                <Shifts preferredShifts={preferredShifts} saveContinue={saveContinue} errors={props.preferenceErrors}/>
                                <TravelDistance travelDistance={travelDistance} saveContinue={saveContinue} errors={props.preferenceErrors}/>
                                <PrefferedWorkdays {...preferredWorkDays} masterWorkdays={masterDatas.preferredWorkDaysRaw} saveContinue={saveContinue} />
                                <Holiday {...holidayPreference} masterHolidays={masterDatas.holidayPreferenceRaw} saveContinue={saveContinue}/>
                                <Covid vaccination={masterDatas.covidVaccine.find(vac => vac.id===vaccination).name} masterVaccine={masterDatas.covidVaccine} saveContinue={saveContinue} errors={props.preferenceErrors}/>
                            </div>
                        </div>
                        <div className="card-footer text-end">
                            <div className="col-lg-12 col-md-12">
                                <a href="#" className="btn btn-light float-left" onClick={(event) => {
                                    event.preventDefault();
                                    dispatch(gotoPreviousTab());
                                }}>Back</a>
                                <a href="#" className="btn btn-primary"> Save </a> &nbsp;
                                <a href="#" className="btn btn-primary" onClick={submitProfile}>Submit Profile</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
    )
}
