import React, {useEffect, useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {useNavigate} from "react-router-dom";
import useConfiguration from '../../hooks/useConfiguration';
import useSessionStorage from "../../hooks/useSessionStorage";
import Header from "../common/Header";
import Sidebar from "../common/Sidebar";
import Footer from "../common/Footer";
import PageHeader from "./pageHeader";
import './profilebuilder.css';
import PersonalInfo from "./PersonalInfo";
import {getCurrentPersonalInfo, savePersonalInfo, resetErrorStatus} from './PersonalInfo/action';
import {getCurrentResumeBuilder, saveResumeBuilder} from "./ResumeBuilder/action";
import {getMasterData, getPreference, savePreference} from "./Preference/action";
import './index.css';
import Loader from "../common/Loader";
import {profileBuilderSelector} from './selector';
import {personalInfoSelector} from "./PersonalInfo/selector";
import {previousTab, gotoTab} from "./action";
import ResumeBuilder from "./ResumeBuilder";
import Preference from "./Preference";
import Summary from "../Summary";
import SaveSuccess from "../SaveSuccess";
import useValidation from "../../hooks/useValidation";
import {validateResume} from './ResumeBuilder/customResumeValidation';
import {resumeBuilderSelector} from "./ResumeBuilder/selector";
import {preferenceSelector} from "./Preference/selector";
import {loginSelector} from "../Login/selector";

export default () => {
    const [registrationErrors, setRegistrationErrors] = useState({});
    const [emergencyContactErrors, setEmergencyContactErrors] = useState({});
    const [referencesErrors, setReferencesErrors] = useState([]);
    const [workHistoryErrors, setWorkHistoryErrors] = useState([]);
    const [educationErrors, setEducationErrors] = useState([]);
    const [resumeBuilderError, setResumeBuilderError] = useState({});
    const [preferenceErrors, setPreferenceErrors] = useState({});
    const dispatch = useDispatch();
    const config = useConfiguration();
    const storage = useSessionStorage();
    const validate = useValidation();
    const navigate = useNavigate();
    const {activeTab, error, errorMessage} = useSelector(profileBuilderSelector);
    const {data} = useSelector(personalInfoSelector);
    const resumeForm = useSelector(resumeBuilderSelector);
    const preferenceForm = useSelector(preferenceSelector);
    const {profileFlag} = useSelector(loginSelector);
    const login = storage.getItem("login");
    const successStage = activeTab === 'success';
    useEffect(() => {
        document.title = "Profile Builder";
    }, []);
    useEffect(() => {
        if (config.baseUrl) {
            if (!login) {
                navigate("/");
            }
        }
    }, [config]);
    const tabStyle = (tabName) => {
        return activeTab === tabName ? 'nav-item active' : 'nav-item';
    };
    const validatePersonalInfo = () => {
        const keys = Object.keys(data);
        const registrationResult = validate("personalInfo", keys.map(key => {
            return {
                name: key, data: data[key]
            }
        }));
        if (registrationResult.valid) {
            setRegistrationErrors({});
        } else {
            setRegistrationErrors(registrationResult.errors);
        }
        return registrationResult.valid;
    }
    const validateEmergencyContact = () => {
        const keys = Object.keys(data.emergencyContact);
        const emergencyResult = validate("emergencyContact", keys.map(key => {
            return {
                name: key, data: data.emergencyContact[key]
            }
        }));
        if (emergencyResult.valid) {
            setEmergencyContactErrors({});
        } else {
            setEmergencyContactErrors(emergencyResult.errors);
        }
        return emergencyResult.valid;
    }
    const validateReferences = () => {
        const validationResults = data.references.map((ref, index) => {
            const keys = Object.keys(ref);
            const anyFilled = keys.some(key => ref[key]);
            //if(index === 0 || anyFilled) {
            if (anyFilled) {
                return validate("references", keys.map(key => {
                    return {
                        name: key, data: ref[key]
                    }
                }));
            } else {
                return {valid: true};
            }
        });
        const valid = validationResults.every(result => result.valid);
        if (valid) {
            setReferencesErrors([]);
        } else {
            setReferencesErrors(validationResults.map(result => result.errors));
        }
        return valid
    }

    const validatePreferences = () => {
        const preferenceKeys = Object.keys(preferenceForm);
        const validationResult = validate("preferences", preferenceKeys.map(key => {
            return {
                name: key, data: preferenceForm[key]
            }
        }));
        if (validationResult.valid) {
            setPreferenceErrors({});
        } else {
            setPreferenceErrors(validationResult.errors);
        }
        return validationResult.valid;
    }

    const validateNoResume = () => {
        if (resumeForm.data.noResume) {
            const workHistoryValidationResult = resumeForm.workHistory.map((ref, index) => {
                const keys = Object.keys(ref);
                const anyFilled = keys.some(key => ref[key]);
                if (index === 0 || anyFilled) {
                    return validate("workHistory", keys.map(key => {
                        return {
                            name: key, data: ref[key]
                        }
                    }));
                } else {
                    return {
                        valid: true
                    }
                }
            });
            const educationValidationResult = resumeForm.educationList.map((ref, index) => {
                const keys = Object.keys(ref);
                const anyFilled = keys.some(key => ref[key]);
                if (index === 0 || anyFilled) {
                    return validate("education", keys.map(key => {
                        return {
                            name: key, data: ref[key]
                        }
                    }));
                } else {
                    return {
                        valid: true
                    }
                }
            });
            const valid = workHistoryValidationResult.every(result => result.valid) &&
                educationValidationResult.every(result => result.valid);
            if (valid) {
                setEducationErrors([]);
                setWorkHistoryErrors([]);
            } else {
                setEducationErrors(educationValidationResult.map(result => result.errors));
                setWorkHistoryErrors(workHistoryValidationResult.map(result => result.errors));
            }
            return valid;
        } else {
            return true;
        }
    }
    const saveContinue = (currentTab, gotoNextTab = true) => {
        window.scrollTo(0, 0)
        dispatch(resetErrorStatus())
        let valid = true;
        if (currentTab === 'personalInfo') {
            if (validatePersonalInfo() && validateEmergencyContact() && validateReferences()) {
                dispatch(savePersonalInfo({
                    url: `${config.baseUrl}/Candidates/savepersonalinfo/`,
                    candidateId: login.candidateId,
                    userId: login.appUserId,
                    gotoNextTab
                }));
            } else {
                valid = false;
            }
        } else if (currentTab === 'resumeBuilder') {
            const resumeFormValid = validateResume(resumeForm);
            if (validateNoResume() && resumeFormValid.isValid) {
                dispatch(saveResumeBuilder({
                    url: `${config.baseUrl}`, candidateId: login.candidateId, userId: login.appUserId, gotoNextTab
                }));
                setResumeBuilderError({});
            } else {
                setResumeBuilderError(resumeFormValid.errors);
                valid = false;
            }
        } else if (currentTab === 'preference') {
            if (validatePreferences()) {
                dispatch(savePreference({
                    url: `${config.baseUrl}`, candidateId: login.candidateId, userId: login.appUserId, gotoNextTab
                }));
            } else {
                valid = false;
            }
        }
        return valid;
    };

    const cancelSummaryEdit = (currentTab) => {
        if (currentTab === 'personalInfo') {
            dispatch(getCurrentPersonalInfo({
                url: `${config.baseUrl}/Candidates/getpersonalinfo`, userId: login.candidateId
            }));
        } else if (currentTab === 'resumeBuilder') {
            dispatch(getCurrentResumeBuilder({
                url: `${config.baseUrl}/Candidates/getjobinfo`, userId: login.candidateId
            }));
        } else if (currentTab === 'preference') {
            dispatch(getPreference(config.baseUrl, login.candidateId));
        }
    };

    const gotoPrevious = () => {
        window.scrollTo(0, 0)
        dispatch(previousTab());
    };

    const navigateToTab = (tabName) => {
        window.scrollTo(0, 0)
        dispatch(gotoTab(tabName));
    }

    const previousDisabled = activeTab === 'personalInfo';
    return (
        <div className="page">
            <div className="page-main">
                <Loader/>
                <Header/>
                <Sidebar/>
                <div className={"main-content app-content mt-0 main-background"}>
                    <div className="side-app">
                        <div className="main-container container-fluid">
                            <PageHeader previous={"Dashboard"} current={"Profile Builder"}/>
                            {
                                activeTab !== 'summary' &&
                                <div className="row">
                                    <div className="col-12">
                                        <div className="card">
                                            {
                                                !successStage &&
                                                <div className="card-header border-bottom-0">
                                                    <h3 className="card-title">Profile Builder</h3>
                                                </div>
                                            }
                                            {
                                                error &&
                                                <>
                                                    <div
                                                        className="toast wrap-input100 align-items-center text-white bg-danger border-0 show"
                                                        role="alert" aria-live="assertive" aria-atomic="true"
                                                        data-bs-autohide="false">
                                                        <div className="d-flex col-6">
                                                            <div className="toast-body">
                                                                {errorMessage || 'Save error! Please try again.'}
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <div>&nbsp;</div>
                                                </>
                                            }
                                            <div className="card-body">
                                                <div id="smartwizard-3" className={"sw-main sw-theme-dots"}>
                                                    {
                                                        !successStage && <ul className={"nav nav-tabs step-anchor"}>
                                                            <li className={tabStyle('personalInfo')}><a
                                                                className={'nav-link'} href="#"
                                                                onFocus={(e) => e.preventDefault()} onClick={(event) => {
                                                                event.preventDefault();
                                                                profileFlag && profileFlag.isPersonalInfoTabCompleted && navigateToTab('personalInfo');
                                                            }}>Personal Info</a></li>
                                                            <li className={tabStyle('resumeBuilder')}><a
                                                                className={'nav-link'} href="#"
                                                                onFocus={(e) => e.preventDefault()} onClick={event => {
                                                                event.preventDefault();
                                                                profileFlag && profileFlag.isJobInfoCompleted && navigateToTab('resumeBuilder');
                                                            }}>Resume Builder</a></li>
                                                            <li className={tabStyle('preference')}><a className={'nav-link'}
                                                                                                      href="#"
                                                                                                      onFocus={e => e.preventDefault()}
                                                                                                      onClick={(event) => {
                                                                                                          event.preventDefault();
                                                                                                          profileFlag && profileFlag.isJobInfoCompleted && navigateToTab('preference');
                                                                                                      }}>Preferences</a>
                                                            </li>
                                                        </ul>
                                                    }
                                                    <div className={"tab-content sw-container"}>
                                                        {
                                                            successStage && <SaveSuccess/>
                                                        }
                                                        {
                                                            activeTab === 'personalInfo' &&
                                                            <div id={"step-personalInfo"}>
                                                                <PersonalInfo registrationErrors={registrationErrors}
                                                                              emergencyContactErrors={emergencyContactErrors}
                                                                              referencesErrors={referencesErrors}/>
                                                            </div>
                                                        }
                                                        {
                                                            activeTab === 'resumeBuilder' &&
                                                            <div id={"step-resumeBuilder"}>
                                                                <ResumeBuilder resumeBuilderError={resumeBuilderError}
                                                                               educationErrors={educationErrors}
                                                                               workHistoryErrors={workHistoryErrors}/>
                                                            </div>
                                                        }
                                                        {
                                                            activeTab === 'preference' &&
                                                            <div id={"step-preference"}>
                                                                <Preference preferenceErrors={preferenceErrors}/>
                                                            </div>
                                                        }
                                                        {!successStage && <>
                                                            <div
                                                                className="btn-toolbar sw-toolbar sw-toolbar-bottom justify-content-end">
                                                                <div className="btn-group me-2 sw-btn-group"
                                                                     role="group">
                                                                    <button className="btn btn-primary sw-btn-next"
                                                                            type="button"
                                                                            onClick={() => saveContinue(activeTab)}>Save
                                                                        and Continue
                                                                    </button>
                                                                </div>
                                                                <div className="btn-group mr-2 sw-btn-group-extra"
                                                                     role="group">
                                                                    <button className="btn btn-danger">Cancel</button>
                                                                </div>
                                                            </div>
                                                            <div
                                                                className="btn-toolbar sw-toolbar sw-toolbar-bottom justify-content-left">
                                                                <div className="btn-group me-2 sw-btn-group prev-margin"
                                                                     role="group">
                                                                    <button
                                                                        className={`btn btn-danger sw-btn-prev ${previousDisabled ? 'disabled' : ''}`}
                                                                        type="button" onClick={gotoPrevious}>Previous
                                                                    </button>
                                                                </div>
                                                            </div>
                                                        </>}
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            }
                            {
                                activeTab === 'summary' && <Summary registrationErrors={registrationErrors}
                                                                    emergencyContactErrors={emergencyContactErrors}
                                                                    referencesErrors={referencesErrors}
                                                                    resumeBuilderError={resumeBuilderError}
                                                                    educationErrors={educationErrors}
                                                                    workHistoryErrors={workHistoryErrors}
                                                                    preferenceErrors={preferenceErrors}
                                                                    cancel={cancelSummaryEdit}
                                                                    saveContinue={saveContinue}/>
                            }
                        </div>
                    </div>
                </div>
            </div>
            <Footer/>
        </div>
    )
}
