import React from "react";
import './input.css';

export default (props) => {

    const [passwordType, setPasswordType] = useState("text");
    const [passwordInput, setPasswordInput] = useState("");
    const handlePasswordChange =(evnt)=>{
        setPasswordInput(evnt.target.value);
    }
    const togglePassword =()=>{
      if(passwordType==="password")
      {
       setPasswordType("text")
       return;
      }
      setPasswordType("password")
    }

    return(
        <>
        <div className="wrap-input100 validate-input input-group is-invalid" data-bs-validate="Valid email is required: ex@abc.xyz">
            <a href="#" className={`input-group-text bg-white text-muted`} onClick={togglePassword}>
                <i className={props.iconClass} aria-hidden="true"></i>
            </a>
            <input className={`input100 border-start-0 form-control ms-0 ${(props.errors && props.errors.length>0) && 'has-errors'}`}
                   type={props.type} placeholder={props.placeholder} required={props.required}
                   autoFocus={props.autoFocus}
                   onChange={(event) => props.updateValue(event.target)} name={props.name}/>
        </div>
            {props.errors && props.errors.map(error => (<div className="invalid-input">{error}</div>))}
        </>
    )
}
