import { setPersonalInfo, setCityState, setMasterData, 
  addSpokenLanguagesList, removeSpokenLanguagesList ,
  setEmergencyContactRelationship,
  setMasterDatas,} from './slice';
import { setError, resetError } from "../slice";
import { setLoading } from "../../common/Loader/slice";
import requestUtil from '../../../helpers/requestUtil';
import { nextTab } from "../action";
import useConfiguration from "../../hooks/useConfiguration"

export function updatePersonalInfo(data) {
  return async dispatch => {
    dispatch(setPersonalInfo(data));
  };
}

export function resetErrorStatus() {
  return async dispatch => {
    dispatch(resetError());
  };
}

export function getCurrentPersonalInfo({url, userId}) {
  return async dispatch => {
    dispatch(setLoading(true));
    try {
      const response = await requestUtil(`${url}/${userId}`, 'GET');

      const emergencyContactRelationship = await requestUtil(`${url}/Dropdown/GetEmergencyContactRelationships`, 'GET');

      dispatch(setPersonalInfo(response));
      dispatch(setMasterDatas({

        emergencyContactRelationship
      }));
    } catch (error) {
      dispatch(setError(error.message))
    }
    dispatch(setLoading(false));
  };
}

export function savePersonalInfo({url, candidateId, userId, gotoNextTab=true}) {
  return async (dispatch, getState) => {
    dispatch(setLoading(true));
    const { data } = getState().personalInfo;
    try {
      await requestUtil(`${url}`, 'POST', {appUserId: userId, candidateId,
        ...data, spokenLanguagesList: data.spokenLanguagesList.map(item => item.id)});
      if(gotoNextTab) {
        dispatch(nextTab());
      }
    } catch (error) {
      dispatch(setError(error.message));
    }
    dispatch(setLoading(false));
  };
}

export function getCityState(url) {
  return async dispatch => {
    try {
      const response = await requestUtil(url, 'GET');
      dispatch(setCityState(response));
    } catch (error) {
      dispatch(setError(error.message))
    }
  };
}

export function getSpokenLanguages(url) {
  return async dispatch => {
    dispatch(setLoading(true));
    try {
      const response = await requestUtil(url, 'GET');
      dispatch(setMasterData({languages: response} ));
    } catch (error) {
      dispatch(setError(error.message))
    }
    dispatch(setLoading(false));
  };
}

export function updateLanguageList(lang, action) {
  return (dispatch) => {
    switch (action) {
      case 'add':
        dispatch(addSpokenLanguagesList(lang));
        break;
      case 'remove':
        dispatch(removeSpokenLanguagesList(lang));
        break;
      default:
        break;
    }
  };

}

export function getMasterData(baseUrl) {
  return async (dispatch) => {
    dispatch(setLoading(true));
    try {

      const emergencyContactRelationship = await requestUtil(`${baseUrl}/Dropdown/GetEmergencyContactRelationships`, 'GET');

      let keyIndex = 0;


      dispatch(setLoading(false));
      dispatch(setMasterDatas({

        emergencyContactRelationship
      }));
    } catch (error) {
      dispatch(setLoading(false));
      dispatch(setError(error.message));
    }
  };
}

export function updateemergencyContactRelationship(value) {
  return (dispatch) => {
    dispatch(setEmergencyContactRelationship(value));
  };
}
