import {createSlice} from "@reduxjs/toolkit";
const tabOrder = ['personalInfo', 'resumeBuilder', 'preference', 'summary'];
const initialState = {
    activeTab: tabOrder[0],
};
export const profileBuilderSlice = createSlice({
    name: 'profileBuilder',
    initialState,
    reducers: {
        gotoNextTab: (state) => {
            state.activeTab = tabOrder[tabOrder.indexOf(state.activeTab) + 1];
        },
        gotoPreviousTab: (state) => {
            state.activeTab = tabOrder[tabOrder.indexOf(state.activeTab) - 1];
        },
    },
});
export const {gotoNextTab, gotoPreviousTab} = profileBuilderSlice.actions;
export default profileBuilderSlice.reducer;
