import {useState} from "react";
import Modal from "./modal";
import PreferredDaysForm from "../ProfileBuilder/Preference/preferredDaysForm";

export default (props) => {
    const [showModal, setShowModal] = useState(false);
    const editWorkDay = (event) => {
        event.preventDefault();
        setShowModal(!showModal);
    };
    const saveWorkday = (event) => {
        event.preventDefault();
        if(props.saveContinue('preference', false)){
            setShowModal(!showModal);
        }
    };
    return (
        <div className="col-md-12 col-xl-12">
            <div className="card">
                {
                    showModal && <Modal saveFunction={saveWorkday} showHide={editWorkDay} label={"Edit Week Days"}>
                        <PreferredDaysForm />
                    </Modal>
                }
                <div className="col-lg-12 col-md-12">
                    <div className="row">
                        <div className="card-header">
                            <div className="col-lg-4 col-md-4">
                                <h3>Preferred Work Days</h3>
                            </div>
                            <div className="col-lg-2 col-md-2">
                                <a id="EditWeek1" href="#" onClick={editWorkDay}>
                                    <i className="fe fe-edit" title="Edit Week Days"></i>
                                </a>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card-body">
                    <div className="form-group">
                        <div className="form-group">
                            {
                                props.preferredWorkDays.map(workDay => (
                                    <div className="col-lg-2 col-md-2">
                                        <label htmlFor="exampleInputname1">{props.masterWorkdays.find(day => day.id===workDay).name}</label>
                                    </div>
                                ))
                            }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
