import { useSelector, useDispatch } from "react-redux";
import {resumeBuilderSelector} from "./selector";
import {updateCategory, loadJobCategories} from "./action";
import DropDown from "../../common/TextField/dropDown";
import React, {useEffect} from "react";
import useConfiguration from "../../../hooks/useConfiguration";

export default () => {
    const dispatch = useDispatch();
    const {categories, masterDatas} = useSelector(resumeBuilderSelector);
    const config = useConfiguration();
    useEffect(() => {
        config.baseUrl && dispatch(loadJobCategories(config.baseUrl, 1));
    }, [config]);
    const updateCategories = (target) => {
        const value = target.value;
        const action = target.action || 'add';
        dispatch(updateCategory(value, action));
    };
    return (
        <div className="card">
            <div className="col-lg-12 col-md-12">
                <div className="row">
                    <div className="card-header">
                        <div className="col-lg-4 col-md-4">
                            <h4>Categories</h4>
                        </div>
                    </div>
                </div>
            </div>
            <div className="card-body">
                <div className="row">
                    <div className="col-lg-12 col-md-12">
                        <div className="form-group">
                            <DropDown name={'categories'} label={'Select Categories*'} id={'categories'}
                                      options={masterDatas.jobCategories} multiple={true} name={'category'} value={categories.selected}
                                      updateValue={updateCategories}/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
