import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { resumeBuilderSelector} from "./selector";
import {updateWorkHistory} from "./action";
import Months from "../../common/Months";
import FromMonths from "../../common/FromMonths";
import Years from "../../common/Years";
import FromYears from "../../common/FromYears";
import TextField from '../../common/TextField/withLabel';

export default (props) => {
    const dispatch = useDispatch();
    const { workHistory } = useSelector(resumeBuilderSelector);
    const [history, setHistory] = React.useState(workHistory);
    useEffect(() => {
        dispatch(updateWorkHistory(history));
    }, [history]);

    const fieldChanged = (index, target) => {
        const {name, value} = target;
        setHistory(prevState => {
            const newHistory = prevState.map(item => ({...item}));
            newHistory[index][name] = value;
            return newHistory;
        });
    };
    const newRow = (event) => {
        event.preventDefault();
        setHistory(prevState => {
            const newHistory = prevState.map(item => ({...item}));
            newHistory.push({
                employer: '',
                fromMonth: '',
                fromYear: '',
                toMonth: '',
                toYear: '',
            });
            return newHistory;
        });
    };
    const removeRow = (index) => {
        setHistory(prevState => {
            const newHistory = [...prevState];
            newHistory.splice(index, 1);
            return newHistory;
        });
    }
    return (
        <div className="col-lg-12 col-md-12" id="divresumeshow">

            <hr className="new4"/>
                <div className="col-lg-12 col-md-12">
                <div class="col-lg-4 col-md-4">
                        <h4>Work History</h4>
                    </div>
                </div>
                <div className="card-body">
                    <div className="row">
                        <div className="col-lg-2 col-md-2">
                            <div className="form-group">
                                <label className="card-title">Employer</label>
                            </div>
                        </div>
                        <div className="col-lg-2 col-md-2">
                            <div className="form-group">
                                <label className="card-title">From Month</label>
                            </div>
                        </div>
                        <div className="col-lg-2 col-md-2">
                            <div className="form-group">
                                <label className="card-title">From Year</label>
                            </div>
                        </div>
                        <div className="col-lg-2 col-md-2">
                            <div className="form-group">
                                <label className="card-title">To Month</label>
                            </div>
                        </div>
                        <div className="col-lg-2 col-md-2">
                            <div className="form-group">
                                <label className="card-title">To Year</label>
                            </div>
                        </div>
                        <div className="col-lg-2 col-md-2">
                            <div className="form-group">
                                <label className="card-title">Delete</label>
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-12 col-md-12" id="divWkClone">
                        {
                            workHistory.map((h, index) => <div className="row">
                                <TextField divClass={"col-lg-2 col-md-2"} noLabel={true}
                                           name="employer" placeholder={"Name"} value={h.employer}
                                           updateValue={(target) => fieldChanged(index, target)} errors={props.errors[index] && props.errors[index]['employer']}/>
                                <FromMonths name={'fromMonth'} value={h.fromMonth}
                                        updateValue={(target) => fieldChanged(index, target)} errors={props.errors[index] && props.errors[index]['fromMonth']}/>
                                <FromYears name={'fromYear'} value={h.fromYear} updateValue={(target) => fieldChanged(index, target)}
                                       errors={props.errors[index] && props.errors[index]['fromYear']} />
                                <Months name={'toMonth'} value={h.toMonth} updateValue={(target) => fieldChanged(index, target)} 
                                        errors={props.errors[index] && props.errors[index]['toMonth']}/>
                                <Years name={'toYear'} value={h.toYear} updateValue={(target) => fieldChanged(index, target)} 
                                       errors={props.errors[index] && props.errors[index]['toYear']}/>
                                <div className="col-lg-1 col-md-1">
                                    <div className="form-group">
                                        <div className="col-sm">
                                            <a onClick={(event) => {
                                                event.preventDefault();
                                                removeRow(index);
                                            }
                                            }><i className="fe fe-trash-2"></i></a>
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    &nbsp;
                                </div>
                            </div>)
                        }
                    </div>
                    <div className="row">
                        <div className="col-lg-4 col-md-2">
                            <a id="btnWKH" href="#" onClick={newRow}>
                                <div className="row">
                                    <div className="col-lg-1 col-md-1">
                                        <i className="icon-2x icon-plus" data-bs-toggle="tooltip" title="" data-bs-original-title="icon-plus" aria-label="Add Work History"></i>
                                    </div>
                                    <div className="col-lg-4 col-md-2">
                                        Add New
                                    </div>
                                </div>
                            </a>
                        </div>
                    </div>
                </div>
        </div>
    )
}
