import { resumeBuilderSelector } from "./selector";
import React, { useState, useEffect } from "react"
import {useDispatch, useSelector} from "react-redux";
import {updateSpeciality} from './action';

export default (props) => {
  const dispatch = useDispatch();
  const {specialities, masterDatas} = useSelector(resumeBuilderSelector);
  const [skills, setSkills] = useState(specialities);
  useEffect(() => {
      dispatch(updateSpeciality(skills));
  }, [skills]);
    const updateSkill = (e, index) => {
      const {value, name} = e.target;
      const newSkills = specialities.map(skill => ({...skill}));
      newSkills[index][name] = value.replace(/\D/g, "");

      setSkills(newSkills);

    }
    const addSkill = () => {
      const newSkills = [...specialities];
      newSkills.push({
        skill: "",
        years: ""
      });
      setSkills(newSkills);
    }
    const removeRow = (index) => {
      const newSkills = [...specialities];
      newSkills.splice(index, 1);
      setSkills(newSkills);
    }


    const errors = props.errors ? props.errors['specialities'] : [];
    return(
        <div id="DivFor_YSpecialties">
            <div id="largemodal_YSpecialties_1">
                <div id="largemodal_YSpecialties_2">
                    <div id="largemodal_YSpecialties_3">
                        <div className="card">
                            <div className="col-lg-12 col-md-12">
                                <div className="row">
                                    <div className="card-header">
                                        <div className="col-lg-12 col-md-12">
                                            <h4>Your Specialties*</h4>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="card-body">
                                {
                                    specialities.map((skill, index) => (
                                        <div className="row" id="displ">
                                            <div className="col-lg-12 col-md-12" id="divSpecialClone1">
                                                <div className="row" id="">
                                                    <div className="col-sm">
                                                    <div>Specialties Name*</div>
                                                        <select className="form-select" data-placeholder="List all that apply"  value={skill.skill} name={"skill"}
                                                                onChange={(event) => updateSkill(event, index)}>
                                                            <option value={""}>List all that apply</option>
                                                            {
                                                                masterDatas.specialities.map((option) => (
                                                                    <option value={option.id} selected={option.id===skill.skill}>{option.name}</option>
                                                                ))
                                                            }
                                                        </select>
                                                        {errors && errors.map(error => (<div className="invalid-input">{error}</div>))}
                                                    </div>

                                                    <div className="col-sm">
                                                    <div>Years of experience*</div>
                                                        <input type="text" className="form-control" id="exampleInputname" placeholder="Years of Experience" name={"years"}
                                                               onChange={(event) => updateSkill(event, index) } value={skill.years}  maxlength="2" />
                                                    </div>
                                                    <div className="col-sm">
                                                        <a href="#" onClick={(event) => {
                                                            event.preventDefault();
                                                            removeRow(index)
                                                        }}><i className="fe fe-trash-2"></i></a>
                                                    </div>
                                                    <div className="row">
                                                        &nbsp;
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ))
                                }
                                <div className="row" id="displ">
                                    <div className="row">
                                        <div className="col-lg-3 col-md-3">
                                            <a id="btnAddSpl" href="#" onClick={(event) => {
                                                event.preventDefault();
                                                addSkill();
                                            }}>
                                                <div className="row">
                                                    <div className="col-lg-1 col-md-3">
                                                        <i className="icon-2x icon-plus" data-bs-toggle="tooltip"
                                                           title="" data-bs-original-title="icon-plus"
                                                           aria-label="Add Specialties"></i>
                                                    </div>
                                                    <div className="col-lg-6 col-md-3">
                                                        Add New
                                                    </div>
                                                </div>
                                            </a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
