import TextField from "../../common/TextField/withLabel";
import React, {useEffect} from "react";
import {personalInfoSelector} from "./selector";
import {updatePersonalInfo} from "./action";
import {useDispatch, useSelector} from "react-redux";

export default (props) => {
    const {errors} = props;
    const { data } = useSelector(personalInfoSelector);
    const dispatch = useDispatch();
    const [form, setForm] = React.useState(data);
    useEffect(() => {
        dispatch(updatePersonalInfo(form));
    }, [form]);
    const updateEmergencyForm = (target) => {
        setForm({
            ...data,
            emergencyContact: {
                ...data.emergencyContact,
                [target.name]: target.value
            }
        });
    };
    return (
        <div className="card-body">
            <div className="row">
            <div class="wrap-input100 validate-input input-group" id="Emailtoggle">
                                                <label for="exampleInputname" class="logintextspace"><h5>First name*</h5></label>
                                            </div>
                <TextField name={'firstName'} label={'First Name*'} placeholder={"First Name"}
                           noLabel={true} updateValue={updateEmergencyForm} value={data.emergencyContact.firstName} errors={errors['firstName']}/>
                <TextField name={'lastName'} label={'Last Name*'} placeholder={"Last Name"}
                           noLabel={true} updateValue={updateEmergencyForm} value={data.emergencyContact.lastName} errors={errors['lastName']}/>
            </div>
            <div className="row">
                <TextField name={'phoneNumber'} placeholder={"Phone Number"} noLabel={true}
                           updateValue={updateEmergencyForm} value={data.emergencyContact.phoneNumber} errors={errors['phoneNumber']} />
                <TextField name={'relationship'} placeholder={"Relationship"} noLabel={true}
                           updateValue={updateEmergencyForm} value={data.emergencyContact.relationship} errors={errors['relationship']} />
            </div>
        </div>
    )
}
