import {useDispatch, useSelector} from "react-redux";
import {preferenceSelector} from "./selector";
import React, {useState} from "react";
import {updateHolidayPreference, updatePreferredWorkDays} from "./action";

export default () => {
    const dispatch = useDispatch();
    const { preferredWorkDays, holidayPreference, masterDatas } = useSelector(preferenceSelector);

    const [holidayRadio, setHolidayRadio] = useState(holidayPreference.preference)

    const setPrefferedWorkDays = (target) => {
        const value = target.value;
        const checked = target.checked;
        const newWorkDays = [...preferredWorkDays];
        if(checked) {
            newWorkDays.push(Number(value));
        } else {
            newWorkDays.splice(newWorkDays.indexOf(Number(value)), 1);
        }
        dispatch(updatePreferredWorkDays(newWorkDays));
    };

    const setHolidayList = (target) => {
        const value = target.value;
        const checked = target.checked;
        const newHoliday = {...holidayPreference};
        newHoliday.options = [...newHoliday.options];
        if(checked) {
            newHoliday.options.push(Number(value));
        } else {
            newHoliday.options.splice(newHoliday.options.indexOf(Number(value)), 1);
        }
        dispatch(updateHolidayPreference(newHoliday));
    };
    const selectHolidayPreference = (target) => {
        const value = target.value;
        const newHoliday = {...holidayPreference, options: [...holidayPreference.options]};
        newHoliday.preference = Number(value);
        if(Number(value) === 0) {
            newHoliday.options = [];
        }
        if(Number(value) === 1) {
            newHoliday.options = [...masterDatas.holidayPreferenceRaw.map(item => item.id)];
        }
        dispatch(updateHolidayPreference(newHoliday));
        setHolidayRadio(value);
    };
    return (
        <div id="DivFor_Week">
            <div id="largemodal_Week_1">
                <div id="largemodal_Week_2">
                    <div id="largemodal_Week_3">
                        <div className="card">
                            <div className="card-header">
                                <h4 className="card-title">Preferred Work Days</h4>
                            </div>
                            <div className="card-body">
                                <div className="row">
                                    {
                                        masterDatas.preferredWorkDays.map((weekday, index)=> (
                                            <>
                                                <div className="col-lg-4 col-md-4">
                                                    <label className="custom-control custom-checkbox mt-4">
                                                    <input type="checkbox" className="custom-control-input" name={weekday.name}
                                                           value={weekday.id} checked={preferredWorkDays.indexOf(weekday.id) > -1} onChange={(event) => setPrefferedWorkDays(event.target)}/>
                                                    <span className="custom-control-label">
                                                        {weekday.name}
                                                    </span>
                                                    </label>
                                                </div>
                                                {(index+1)%4 === 0 && <br/>}
                                            </>
                                        ))
                                    }
                                </div>
                            </div>
                            <div className="card-header">
                                <h4 className="card-title">Willing to work on the following holidays</h4>
                            </div>
                            <div className="card-body">
                                <div className="row">
                                    <div className="col-lg-4 col-md-4">
                                        <div className="form-group">
                                            <div className="custom-control custom-radio">
                                                <input className="custom-control-input" type="radio" id="Holidays1" value={0} name="customRadio"
                                                       onChange={(event) => selectHolidayPreference(event.target)} checked={holidayPreference.preference === 0}/>
                                                <label htmlFor="Holidays1" className="custom-control-label">No Holidays</label>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-lg-4 col-md-4">
                                        <div className="form-group">
                                            <div className="custom-control custom-radio">
                                                <input className="custom-control-input" type="radio" id="Holidays2" value={1} name="customRadio"
                                                       onChange={(event) => selectHolidayPreference(event.target)} checked={holidayPreference.preference === 1}/>
                                                <label htmlFor="Holidays2" className="custom-control-label">All Holidays</label>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-lg-4 col-md-4">
                                        <div className="form-group">
                                            <div className="custom-control custom-radio">
                                                <input className="custom-control-input" type="radio" id="Holidays3" value={2} name="customRadio"
                                                       onChange={(event) => selectHolidayPreference(event.target)} checked={holidayPreference.preference === 2} />
                                                <label htmlFor="Holidays3" className="custom-control-label">Willing to work on the following holidays</label>
                                            </div>
                                        </div>
                                    </div>
                                    {
                                        ( holidayRadio === '1') && <div className="card-body" id="Someholidayslist">
                                            <div className="form-group">
                                                {
                                                    Object.keys(masterDatas.holidayPreference).map((holiday) => {
                                                        return (
                                                            <div className={"row"}>
                                                                {
                                                                    masterDatas.holidayPreference[holiday].map(h => (
                                                                        <div className={"col-lg-4 col-md-4"} >
                                                                            <label className="custom-control custom-checkbox mt-4">
                                                                                <input type="checkbox" className="custom-control-input" name={h.name}
                                                                                       value={h.id} checked={holidayPreference.options.indexOf(h.id) > -1} onChange={(event) => setHolidayList(event.target)} disabled />
                                                                                <span className="custom-control-label">
                                                                                    {h.name}
                                                                                 </span>
                                                                            </label>
                                                                        </div>
                                                                    ))
                                                                }
                                                            </div>
                                                        )
                                                    })
                                                }
                                            </div>
                                        </div>
                                    }
                                    {
                                        (holidayRadio === '2') && <div className="card-body" id="Someholidayslist">
                                            <div className="form-group">
                                                {
                                                    Object.keys(masterDatas.holidayPreference).map((holiday) => {
                                                        return (
                                                            <div className={"row"}>
                                                                {
                                                                    masterDatas.holidayPreference[holiday].map(h => (
                                                                        <div className={"col-lg-4 col-md-4"} >
                                                                            <label className="custom-control custom-checkbox mt-4">
                                                                                <input type="checkbox" className="custom-control-input" name={h.name}
                                                                                       value={h.id} onChange={(event) => setHolidayList(event.target)}/>
                                                                                <span className="custom-control-label">
                                                                                    {h.name}
                                                                                 </span>
                                                                            </label>
                                                                        </div>
                                                                    ))
                                                                }
                                                            </div>
                                                        )
                                                    })
                                                }
                                            </div>
                                        </div>
                                    }
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
