import {createSlice} from "@reduxjs/toolkit";

const tabOrder = ['personalInfo', 'resumeBuilder', 'preference', 'summary', 'success'];
const initialState = {
    activeTab: tabOrder[0],
    error: false,
    errorMessage: null
};
export const profileBuilderSlice = createSlice({
    name: 'profileBuilder',
    initialState,
    reducers: {
        gotoNextTab: (state) => {
            state.activeTab = tabOrder[tabOrder.indexOf(state.activeTab) + 1];
        },
        gotoPreviousTab: (state) => {
            state.activeTab = tabOrder[tabOrder.indexOf(state.activeTab) - 1];
        },
        setError: (state, action) => {
            state.error = true;
            state.errorMessage = action.payload;
        },
        resetError: (state) => {
            state.error = false;
            state.errorMessage = null;
        },
        setCurrentTab: (state, action) => {
            state.activeTab = action.payload;
        }
    },
});
export const {gotoNextTab, gotoPreviousTab, setError, resetError, setCurrentTab} = profileBuilderSlice.actions;
export default profileBuilderSlice.reducer;
