import {useState} from "react";
import Modal from "./modal";
import SpecialitiesForm from '../ProfileBuilder/ResumeBuilder/specialities';

export default (props) => {
    const [showModal, setShowModal] = useState(false);
    const editSpecialities = (event) => {
        event.preventDefault();
        setShowModal(!showModal);
        if (showModal) {
            props.cancel('resumeBuilder');
        }
    };
    const saveSpecialities = (event) => {
        event.preventDefault();
        if (props.saveContinue('resumeBuilder', false)) {
            setShowModal(!showModal);
        }
    };
    const getSkillName = (skill) => {
        if (skill && skill !== "") {
            const masterSkill = props.masterDatas.specialities.find(speciality => speciality.id === Number(skill));
            if (masterSkill) {
                return masterSkill.name;
            }
        }
        return '';
    };
    return (
        <div className="col-md-12 col-xl-12">
            <div className="card">
                {
                    showModal &&
                    <Modal label={"Your Specialties"} showHide={editSpecialities} saveFunction={saveSpecialities}>
                        <SpecialitiesForm/>
                    </Modal>
                }
                <div className="col-lg-12 col-md-12">
                    <div className="row">
                        <div className="card-header">
                            <div className="col-lg-4 col-md-4">
                                <b><h3>Your Specialties</h3></b>
                            </div>
                            <div className="col-lg-2 col-md-2">
                                <a id="EditYSpecialties" href="#" onClick={editSpecialities}>
                                    <i className="fe fe-edit" title="Edit Specialties"></i>
                                </a>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card-body">
                    <div className="form-group">
                        <div className="row">
                            <div className="row">
                                <div className="col-lg-6 col-md-3">
                                    <label className="form-label">Specialties Name</label>
                                </div>
                                <div className="col-lg-4 col-md-3">
                                    <label className="form-label">Years Of Experience</label>
                                </div>
                            </div>
                            {
                                props.specialities.map(speciality => (
                                    <div className="row">
                                        <div className="col-lg-6 col-md-3">
                                            <label>{getSkillName(speciality.skill)}</label>
                                        </div>
                                        <div className="col-lg-4 col-md-3">
                                            {speciality.years}
                                        </div>
                                    </div>
                                ))
                            }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
