import TextField from "../../common/TextField/withLabel";
import React from "react";
import {useEffect} from "react";
import {useSelector, useDispatch} from "react-redux";
import {personalInfoSelector} from "./selector";
import {updatePersonalInfo} from "./action";

export default () => {
    const { data } = useSelector(personalInfoSelector);
    const dispatch = useDispatch();
    const [form, setForm] = React.useState(data);
    useEffect(() => {
        dispatch(updatePersonalInfo(form));
    }, [form]);
    const updateReferenceValue = (target) => {
        const id = target.id.split('-')[1];
        const name = target.id.split('-')[0];
        const newReference = [...form.references];
        const toUpdate = newReference.map(ref => {
            if (ref.id == id) {
                return {
                    ...ref,
                    [name]: target.value
                }
            }
            return ref;
        });
        setForm({
            ...form,
            references: toUpdate
        });
    };
    const removereferences = (id) => {
        setForm({
            ...form,
            references: form.references.filter(ref => ref.id !== id)
        });
    };
    const addNewRow = () => {
        setForm({
            ...form,
            references: [
                ...form.references,
                {
                    id: form.references.length + 1,
                    firstName: '',
                    lastName: '',
                    phone: ''
                }
            ]
        });
    };
    return (
        <div className="card-body">
            <div className="col-lg-8 col-md-8">
                {
                    data.references.map((reference, index) => (
                        <div className="row" id="direferences" key={reference.id}>
                            <div className="row">
                                <TextField name={`firstName-${reference.id}`} label={'First Name*'}
                                           placeholder={"First Name"} noLabel={false}
                                           divClass={"col-lg-3 col-md-3"}
                                           value={reference.firstName} updateValue={updateReferenceValue}/>
                                <TextField name={`lastName-${reference.id}`} label={'Last Name*'}
                                           placeholder={"Last Name"} noLabel={false}
                                           divClass={"col-lg-3 col-md-3"}
                                           value={reference.lastName} updateValue={updateReferenceValue}/>
                                <TextField name={`phoneNumber-${reference.id}`} label={'Phone Number*'}
                                           placeholder={"Phone Number"} noLabel={false}
                                           divClass={"col-lg-3 col-md-3"}
                                           value={reference.phoneNumber} updateValue={updateReferenceValue}/>
                                <div className="col-sm deleteIconMargin">
                                    <a onClick={() => removereferences(reference.id)}><i
                                        className="fe fe-trash-2"></i></a>
                                </div>
                            </div>
                        </div>
                    ))
                }
                <div className="row">
                    <div className="col-lg-6 col-md-3">
                        <a id="btnreferences" href="#">
                            <div className="row">
                                <div className="col-lg-1 col-md-3">
                                    <i className="icon-2x icon-plus" data-bs-toggle="tooltip" title="" data-bs-original-title="icon-plus"
                                       aria-label="Add References" style={{"font-size": "20px"}}></i>
                                </div>
                                <div className="col-lg-6 col-md-3" onClick={addNewRow}>
                                    Add New
                                </div>
                            </div>
                        </a>
                    </div>
                </div>
            </div>
        </div>
    )
}
