import Checkbox from "../../common/Checkbox";
import {useDispatch, useSelector} from "react-redux";
import {preferenceSelector} from "./selector";
import {useState} from "react";
import {updateHolidayPreference, updatePreferredWorkDays} from "./action";

export default () => {
    const weekdays1 = ["All", "Sunday", "Monday", "Tuesday","Wednesday", "Thursday", "Friday", "Saturday"];
    const holidays = {
        row1: ["New Year's Eve", "Christmas Day", "Birthday of Martin Luther King, Jr."],
        row2: ["New Year's Day - January 1st", "Christmas Eve", "Juneteenth - June 19th"],
        row3: ["Independence Day - July 4th", "Labour day - First Monday of September", "Thanksgiving Eve"],
        row4: ["Columbus day - Second Monday of October", "Veterans Day", "Thanksgiving Day"],
        row5: ["Memorial Day - Last Monday of May", "Washington's Birthday - Third Monday of February"]
    };
    const dispatch = useDispatch();
    const { preferredWorkDays, holidayPreference } = useSelector(preferenceSelector);

    const [holidayRadio, setHolidayRadio] = useState("")

    const setPrefferedWorkDays = (target) => {
        const name = target.name;
        const value = target.value;
        const newWorkDays = [...preferredWorkDays];
        if(value) {
            newWorkDays.push(name);
        } else {
            newWorkDays.splice(newWorkDays.indexOf(name), 1);
        }
        dispatch(updatePreferredWorkDays(newWorkDays));
    };

    const setHolidayList = (target) => {
        const name = target.name;
        const value = target.value;
        const newHoliday = {...holidayPreference};
        newHoliday.options = [...newHoliday.options];
        if(value) {
            newHoliday.options.push(name);
        } else {
            newHoliday.options.splice(newHoliday.options.indexOf(name), 1);
        }
        dispatch(updateHolidayPreference(newHoliday));
    };
    const selectHolidayPreference = (target) => {
        const value = target.value;
        const newHoliday = {...holidayPreference, options: [...holidayPreference.options]};
        newHoliday.preference = value;
        if(value !== 'choose') {
            newHoliday.options = [];
        }
        dispatch(updateHolidayPreference(newHoliday));
        setHolidayRadio(value);
    };
    return (
        <div id="DivFor_Week">
            <div id="largemodal_Week_1">
                <div id="largemodal_Week_2">
                    <div id="largemodal_Week_3">
                        <div className="card">
                            <div className="card-header">
                                <h4 className="card-title">Preferred Work Days</h4>
                            </div>
                            <div className="card-body">
                                <div className="row">
                                    {
                                        weekdays1.map((weekday, index)=> (
                                            <>
                                                <div className="col-lg-3 col-md-3">
                                                    <div className="form-group">
                                                        <Checkbox name={weekday} value={weekday} updateValue={setPrefferedWorkDays} checked={preferredWorkDays.indexOf(weekday) > -1}>
                                                            <label htmlFor={weekday} className="custom-control-label">{weekday}</label>
                                                        </Checkbox>
                                                    </div>
                                                </div>
                                                {(index+1)%4 === 0 && <br/>}
                                            </>
                                        ))
                                    }
                                </div>
                            </div>
                            <div className="card-header">
                                <h4 className="card-title">Willing to work on the following holidays</h4>
                            </div>
                            <div className="card-body">
                                <div className="row">
                                    <div className="col-lg-4 col-md-4">
                                        <div className="form-group">
                                            <div className="custom-control custom-radio">
                                                <input className="custom-control-input" type="radio" id="Holidays1" value="no-holiday" name="customRadio"
                                                       onChange={(event) => selectHolidayPreference(event.target)} />
                                                <label htmlFor="Holidays1" className="custom-control-label">No Holidays</label>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-lg-4 col-md-4">
                                        <div className="form-group">
                                            <div className="custom-control custom-radio">
                                                <input className="custom-control-input" type="radio" id="Holidays2" value="all-holiday" name="customRadio"
                                                       onChange={(event) => selectHolidayPreference(event.target)}/>
                                                <label htmlFor="Holidays2" className="custom-control-label">All Holidays</label>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-lg-4 col-md-4">
                                        <div className="form-group">
                                            <div className="custom-control custom-radio">
                                                <input className="custom-control-input" type="radio" id="Holidays3" value="choose" name="customRadio"
                                                       onChange={(event) => selectHolidayPreference(event.target)}/>
                                                <label htmlFor="Holidays3" className="custom-control-label">Willing to work on the following holidays</label>
                                            </div>
                                        </div>
                                    </div>
                                    {
                                        holidayRadio === 'choose' && <div className="card-body" id="Someholidayslist">
                                            <div className="form-group">
                                                {
                                                    Object.keys(holidays).map((holiday) => {
                                                        return (
                                                            <div className={"row"}>
                                                                {
                                                                    holidays[holiday].map(h => (
                                                                        <div className={"col-lg-4 col-md-4"} >
                                                                            <Checkbox name={h} value={h} updateValue={setHolidayList}>
                                                                                <label htmlFor={h}>{h}</label>
                                                                            </Checkbox>
                                                                        </div>
                                                                    ))
                                                                }
                                                            </div>
                                                        )
                                                    })
                                                }
                                            </div>
                                        </div>
                                    }
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
