import React, {useState, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import {updateEducation} from "./action";
import {resumeBuilderSelector} from "./selector";
import TextField from "../../common/TextField/withLabel";
import Months from "../../common/Months";
import Years from "../../common/Years";

export default () => {
    const dispatch = useDispatch();
    const { educationList } = useSelector(resumeBuilderSelector);
    const [edu, setEdu] = React.useState(educationList);
    useEffect(() => {
        dispatch(updateEducation(edu));
    }, [edu]);
    const fieldChanged = (index, target) => {
        const {name, value} = target;
        setEdu(prevState => {
            const newEdu = prevState.map(item => ({...item}));
            newEdu[index][name] = value;
            return newEdu;
        });
    };
    const newRow = (event) => {
        event.preventDefault();
        setEdu(prevState => {
            const newEdu = prevState.map(item => ({...item}));
            newEdu.push({
                name: '',
                fromMonth: '',
                fromYear: '',
                toMonth: '',
                toYear: '',
            });
            return newEdu;
        });
    };
    const removeRow = (index) => {
        setEdu(prevState => {
            const newEdu = [...prevState];
            newEdu.splice(index, 1);
            return newEdu;
        });
    }

    return (
        <>
            <hr className={"new4"} />
            <div className="col-lg-12 col-md-12">
                <div className="row">
                    <div className="col-lg-4 col-md-4">
                        <h4>Education</h4>
                    </div>
                </div>
            </div>
            <div className="card-body">
                <div className="row">
                    <div className="col-lg-2 col-md-2">
                        <div className="form-group">
                            <label className="card-title">Education</label>
                        </div>
                    </div>
                    <div className="col-lg-2 col-md-2">
                        <div className="form-group">
                            <label className="card-title">From Month</label>
                        </div>
                    </div>
                    <div className="col-lg-2 col-md-2">
                        <div className="form-group">
                            <label className="card-title">From Year</label>
                        </div>
                    </div>
                    <div className="col-lg-2 col-md-2">
                        <div className="form-group">
                            <label className="card-title">To Month</label>

                        </div>
                    </div>
                    <div className="col-lg-2 col-md-2">
                        <div className="form-group">
                            <label className="card-title">To Year</label>
                        </div>
                    </div>
                    <div className="col-lg-2 col-md-2">
                        <div className="form-group">
                            <label className="card-title">Delete</label>
                        </div>
                    </div>
                </div>
                <div className="col-lg-12 col-md-12" id="divEdClone">
                    {
                        edu.map((e, index) =>
                                <div className="row">
                                    <TextField divClass={"col-lg-2 col-md-2"} noLabel={true} name="name" placeholder={"Type Here"} value={e.name}
                                               updateValue={(target) => fieldChanged(index, target)}/>
                                    <Months name={'fromMonth'} value={e.fromMonth} updateValue={(target) => fieldChanged(index, target)}/>
                                    <Years name={'fromYear'} value={e.fromYear} updateValue={(target) => fieldChanged(index, target)} />
                                    <Months name={'toMonth'} value={e.toMonth} updateValue={(target) => fieldChanged(index, target)} addPresent={true}/>
                                    <Years name={'toYear'} value={e.toYear} updateValue={(target) => fieldChanged(index, target)} addPresent={true}/>
                                    <div className="col-lg-1 col-md-1">
                                        <div className="form-group">
                                            <div className="col-sm">
                                                <a onClick={(event) => {
                                                    event.preventDefault();
                                                    removeRow(index);
                                                }}><i className="fe fe-trash-2"></i></a>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        &nbsp;
                                    </div>
                                </div>
                        )
                    }
                </div>
                <div className="row">
                    <div className="col-lg-4 col-md-2">
                        <a id="btnEd" href="#" onClick={newRow}>
                            <div className="row">
                                <div className="col-lg-1 col-md-1">
                                    <i className="icon-2x icon-plus" data-bs-toggle="tooltip" title=""
                                       data-bs-original-title="icon-plus" aria-label="Add Education"></i>
                                </div>
                                <div className="col-lg-4 col-md-2">
                                    Add New
                                </div>
                            </div>
                        </a>
                    </div>
                </div>
            </div>
        </>
    )
}
