import DropDown from "../../common/TextField/dropDown";
import React, {useState, useEffect} from "react";
import {updateCertification, updatePrimaryCertification} from "./action";
import { useSelector, useDispatch } from "react-redux";
import {resumeBuilderSelector} from "./selector";

export default (props) => {
    const dispatch = useDispatch();
    const {certifications, masterDatas} = useSelector(resumeBuilderSelector);
    const [certs, setCerts] = useState(certifications);

    useEffect(() => {
        dispatch(updateCertification(certs));
    }, [certs]);
    const updateCertValue = (target) => {
        const value = target.value;
        const action = target.action || 'add';
        dispatch(updatePrimaryCertification(value, action));
    };

    const updateOtherCertValue = (target, index) => {
        const newCerts = [...certifications.others];
        newCerts[index] = target.value;
        setCerts({
            ...certifications,
            others: newCerts
        });
    };

    const removeRow = (index) => {
        const newCerts = [...certifications.others];
        newCerts.splice(index, 1);
        setCerts({
            ...certifications,
            others: newCerts
        });
    };

    const addRow = (event) => {
        event.preventDefault();
        const newCerts = [...certifications.others];
        newCerts.push("");
        setCerts({
            ...certifications,
            others: newCerts
        });
    };

    return (
        <div id="DivFor_Certification">
            <div id="largemodal_Certification_1">
                <div id="largemodal_Certification_2">
                    <div id="largemodal_Certification_3">
                        <div className="card">
                            <div className="col-lg-12 col-md-12">
                                <div className="row">
                                    <div className="card-header">
                                        <div className="col-lg-4 col-md-4">
                                            <h4>Certifications*</h4>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="card-body">
                                <DropDown name={'certifications'} label={'Select all that apply*'} id={'certifications'}
                                          options={masterDatas.certifications} multiple={true} name={'primary'} value={certifications.primary}
                                          updateValue={updateCertValue} isObject={true} divClass={'col-lg-12 col-md-12'}
                                          errors={(props.errors && props.errors.certifications) ? props.errors.certifications['primary'] : []}/>
                            </div>
                            <div className="card-body">
                                <div className="col-lg-12 col-md-12">
                                    <div className="row">
                                        <div className="col-lg-12 col-md-12">
                                            <h4>Other Certifications </h4>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="card-body">
                                <div className="col-lg-12 col-md-12">
                                    {
                                        certifications.others.map((cert, index) => (
                                            <div className="row">
                                                <div className="col-lg-12 col-md-12" id="divcloneCertifications">
                                                    <div className="row">
                                                        <div className="col-sm">
                                                            <input type="text" value={cert} className="form-control" id="othercertifyname"
                                                                   placeholder="List other certifications" onChange={(event) => updateOtherCertValue(event.target, index)}/>
                                                        </div>
                                                        <div className="col-sm">
                                                            <a onClick={(event => {
                                                                event.preventDefault();
                                                                removeRow(index);
                                                            })}><i className="fe fe-trash-2"></i></a>
                                                        </div>
                                                        <div className="row">
                                                            &nbsp;
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        ))
                                    }
                                    <div className="row">
                                        <div className="col-lg-3 col-md-3">
                                            <a id="btnAddothCertifications" href="#" onClick={addRow}>
                                                <div className="row">
                                                    <div className="col-lg-1 col-md-3">
                                                        <i className="icon-2x icon-plus" data-bs-toggle="tooltip" title="" data-bs-original-title="icon-plus"
                                                           aria-label="Add References" style={{"font-size": "20px"}}></i>
                                                    </div>
                                                    <div className="col-lg-6 col-md-3">
                                                        Add New
                                                    </div>
                                                </div>
                                            </a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
