import {useState} from "react";
import EmergencyContactForm from "../ProfileBuilder/PersonalInfo/emergencyContactForm";
import Modal from "./modal";

export default (props) => {
    const [showModal, setShowModal] = useState(false);
    const saveEmergencyContact = (event) => {
        event.preventDefault();
        if(props.saveContinue('personalInfo', false)){
            setShowModal(!showModal);
        }
    };
    const edieEmergencyContact = (event) => {
        event.preventDefault();
        setShowModal(!showModal);
    }
    return (
        <div className="col-md-12 col-xl-12">
            <div className="card">
                {
                    showModal && <Modal label={'Emergency Contact'} showHide={edieEmergencyContact} saveFunction={saveEmergencyContact}>
                        <EmergencyContactForm errors={props.errors}/>
                    </Modal>
                }
                <div className="col-lg-12 col-md-12">
                    <div className="row">
                        <div className="card-header">
                            <div className="col-lg-4 col-md-4">
                                <b><h3>Emergency Contact</h3></b>
                            </div>
                            <a id="EditEC" href="#" onClick={edieEmergencyContact}>
                                <i className="fe fe-edit" title="Edit Emergency Contact"></i>
                            </a>
                        </div>
                    </div>
                </div>

                <div className="card-body">
                    <div className="form-group">
                        <div className="row">
                            <div className="col-lg-4 col-md-4">
                                <label htmlFor="exampleInputname1">{props.emergencyContact.firstName} {props.emergencyContact.lastName}</label>
                            </div>
                            <div className="col-lg-3 col-md-3">
                                <label htmlFor="exampleInputname1">{props.emergencyContact.phoneNumber}</label>
                            </div>
                            <div className="col-lg-3 col-md-3">
                                <label htmlFor="exampleInputname1">{props.emergencyContact.relationship[0].name}</label>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
