import TextField from "../../common/TextField/withLabel";
import React, {useEffect} from "react";
import {personalInfoSelector} from "./selector";
import {updatePersonalInfo} from "./action";
import {useDispatch, useSelector} from "react-redux";
import TextFieldFormatted from "../../common/TextFieldPhoneNumber";
import DropDown from "../../common/TextField/dropDown";
import {updateemergencyContactRelationship} from "./action";

export default (props) => {
    const {errors} = props;
    const { data } = useSelector(personalInfoSelector);
    const dispatch = useDispatch();
    const [form, setForm] = React.useState(data);
    this.state = {
        options: [{name: 'Option 1️⃣', id: 1},{name: 'Option 2️⃣', id: 2}]
    };

    const { emergencyContact, masterDatas } = useSelector(personalInfoSelector);

    const setEmergencyContactRelationship = (target) => {
        const value = target.value;
        dispatch(updateemergencyContactRelationship(value));
    };

    useEffect(() => {
        dispatch(updatePersonalInfo(form));
    }, [form]);
    const updateEmergencyForm = (target) => {
        setForm({
            ...data,
            emergencyContact: {
                ...data.emergencyContact,
                [target.name]: target.value
            }
        });
    };
    return (
        <div className="card-body">
            <div className="row">
                <TextField name={'firstName'} label={'First Name*'} placeholder={"First Name"}
                            updateValue={updateEmergencyForm} value={data.emergencyContact.firstName} errors={errors['firstName']}/>


                <TextField name={'lastName'} label={'Last Name*'} placeholder={"Last Name"}
                            updateValue={updateEmergencyForm} value={data.emergencyContact.lastName} errors={errors['lastName']}/>
            </div>
            <div className="row">
                <TextField name={'phoneNumber'} label={'Phone Number*'} placeholder={"Phone Number"} 
                           updateValue={updateEmergencyForm} value={data.emergencyContact.phoneNumber} errors={errors['phoneNumber']} />
                <TextField name={'relationship'} label={'Relationship*'} placeholder={"Relationship"} 
                           updateValue={updateEmergencyForm} value={data.emergencyContact.relationship} errors={errors['relationship']} />

                <DropDown name={"relationship"} label={'Relationship*'}
                          placeholder={"Relationship"} 
                          value={data.emergencyContact.relationship} 
                           showArrow={true} isObject={true} multiple={false} errors={errors['relationship']} />

<DropDown name={"relationship"} label={"Relationship*"}
        options={this.stat.options} value={[data.emergencyContact.relationship]}
        id={"relationship"}
        updateValue={setEmergencyContactRelationship} isObject={true} multiple={false} errors={props.errors ? props.errors['relationship'] : []}/>

            </div>
        </div>
    )
}
