import {createSlice} from "@reduxjs/toolkit";
const initialState = {

    data: {
        firstName: '',
        lastName: '',
        email: '',
        zipcode: '',
        streetAddress: '',
        city: '',
        stateName: '',
        cellPhoneNumber: '',
        homePhoneNumber: '',
        alternatePhoneNumber: '',
        emergencyContact: {
            firstName: '',
            lastName: '',
            phoneNumber: '',
            relationship: ''
        },
        references: [
            {
                firstName: '',
                lastName: '',
                phoneNumber: '',
            }
        ],
        spokenLanguagesList: [],
        jobDomainId: 1,
        jobTypeId: 1
    },
    masterData:{
        languages: [],
        emergencyContactRelationship: []
    },
};
export const personalInfoSlice = createSlice({
    name: 'personalInfo',
    initialState,
    reducers: {
        setPersonalInfo: (state, action) => {
            state.data = action.payload;

            if(state.masterData.languages.length >0){
                state.data.spokenLanguagesList = state.masterData.languages.filter(language => state.data.spokenLanguagesList.find(lang => {
                    if(lang.id){
                        return lang.id === language.id
                    } else {
                        return lang === language.id
                    }
                }));
            }
            if(!state.data.references || state.data.references.length === 0){
                state.data.references = [{
                    firstName: '',
                    lastName: '',
                    phoneNumber: '',
                }]
            }

            state.personalInfoError = false;

            //Below Code added for Relationship Dropdown control code on - 05122022
            if(!state.data.emergencyContact){
                state.data.emergencyContact.relationship = state.data.emergencyContact.relationship;
            }




        },
        setCityState: (state, action) => {
            state.data.city = action.payload.city;
            state.data.stateName = action.payload.state;
        },
        setMasterData: (state, action) => {           
            if(action.payload.languages){
                state.masterData.languages = action.payload.languages;
                if(state.data.spokenLanguagesList.length > 0){
                    state.data.spokenLanguagesList = state.data.spokenLanguagesList.map(lang => {
                        if(!lang.id){
                            return action.payload.languages.find(l => l.id === lang);
                        } else {
                            return lang;
                        }
                    })
                }
            }

        },
        addSpokenLanguagesList: (state, action) => {
            const newLanguage = state.data.spokenLanguagesList ? [...state.data.spokenLanguagesList, action.payload] : [action.payload];
            state.data.spokenLanguagesList = newLanguage;
        },
        removeSpokenLanguagesList: (state, action) => {
            const newLanguage = state.data.spokenLanguagesList.filter(item => item.id !== action.payload.id);
            state.data.spokenLanguagesList = [...newLanguage];
        },

        setMasterDataEmergency: (state, action) => { 
            if(action.payload.emergencyContactRelationshipcyContact){
                state.masterData.emergencyContactRelationship = action.payload.emergencyContactRelationshipcyContact;
                state.masterData.emergencyContactRelationshipList=state.masterData.emergencyContactRelationship;
                /*if(state.data.emergencyContactRelationshipList.length > 0){
                    state.data.emergencyContactRelationshipList = state.data.emergencyContactRelationshipList.map(lang => {
                        if(!lang.id){
                            return action.payload.emergencyContactRelationship.find(l => l.id === lang);
                        } else {
                            return lang;
                        }
                    })
                }*/
            }
        },


        addEmergencyContactList: (state, action) => {
            const newEmergencyContact = state.data.emergencyContactRelationshipListcyContactList ? [...state.data.emergencyContactRelationshipListcyContactList, action.payload] : [action.payload];
            state.data.emergencyContact.relationship = newEmergencyContact;
            //console.log("Relationship:"+state.data.emergencyContact.relationship);
            //console.log("Relationship1:"+action.payload.name);
            //state.data.emergencyContact.relationship = action.payload.name;//state.data.emergencyContactRelationshipListcyContactList;//action.payload.name;

        },
        removeEmergencyContactList: (state, action) => {
            const newEmergencyContact = state.data.emergencyContactRelationshipListcyContactList.filter(item => item.id !== action.payload.id);
            state.data.emergencyContact.relationship = [...newEmergencyContact];
        },

        setEmergencyRelationship: (state, action) => {
            state.data.emergencyContact.relationship = action.payload.name;
        },

    }
});
export const { setPersonalInfo,setCityState, setMasterData, setEmergencyRelationship,addSpokenLanguagesList, removeSpokenLanguagesList,setMasterDataEmergency,addEmergencyContactList,removeEmergencyContactList} = personalInfoSlice.actions;
export default personalInfoSlice.reducer;
