import {createSlice} from "@reduxjs/toolkit";
const initialState = {
    emergencyContactRelationshipList:{},
    data: {
        firstName: '',
        lastName: '',
        email: '',
        zipcode: '',
        streetAddress: '',
        city: '',
        stateName: '',
        cellPhoneNumber: '',
        homePhoneNumber: '',
        alternatePhoneNumber: '',
        emergencyContact: {
            firstName: '',
            lastName: '',
            phoneNumber: '',
            relationship: ''
        },
        references: [
            {
                firstName: '',
                lastName: '',
                phoneNumber: '',
            }
        ],
        spokenLanguagesList: [],
        jobDomainId: 1,
        jobTypeId: 1
    },
    masterData:{
        languages: [],
        emergencyContactRelationshipList: [],
    },
};
export const personalInfoSlice = createSlice({
    name: 'personalInfo',
    initialState,
    reducers: {
        setPersonalInfo: (state, action) => {
            state.data = action.payload;

            if(state.masterData.languages.length >0){
                state.data.spokenLanguagesList = state.masterData.languages.filter(language => state.data.spokenLanguagesList.find(lang => {
                    if(lang.id){
                        return lang.id === language.id
                    } else {
                        return lang === language.id
                    }
                }));
            }
            if(!state.data.references || state.data.references.length === 0){
                state.data.references = [{
                    firstName: '',
                    lastName: '',
                    phoneNumber: '',
                }]
            }

            if(state.masterData.emergencyContactRelationshipList.length >0){
                state.data.emergencyContactRelationshipList = state.masterData.emergencyContactRelationshipList.filter(language => state.data.emergencyContactRelationshipList.find(lang => {
                    if(lang.id){
                        return lang.id === language.id
                    } else {
                        return lang === language.id
                    }
                }));
            }

            state.personalInfoError = false;


        },
        setCityState: (state, action) => {
            state.data.city = action.payload.city;
            state.data.stateName = action.payload.state;
        },
        setMasterData: (state, action) => {           
            if(action.payload.languages){
                state.masterData.languages = action.payload.languages;
                if(state.data.spokenLanguagesList.length > 0){
                    state.data.spokenLanguagesList = state.data.spokenLanguagesList.map(lang => {
                        if(!lang.id){
                            return action.payload.languages.find(l => l.id === lang);
                        } else {
                            return lang;
                        }
                    })
                }
            }
        },
        addSpokenLanguagesList: (state, action) => {
            const newLanguage = state.data.spokenLanguagesList ? [...state.data.spokenLanguagesList, action.payload] : [action.payload];
            state.data.spokenLanguagesList = newLanguage;
        },
        removeSpokenLanguagesList: (state, action) => {
            const newLanguage = state.data.spokenLanguagesList.filter(item => item.id !== action.payload.id);
            state.data.spokenLanguagesList = [...newLanguage];
        },


    }
});
export const { setPersonalInfo,setCityState, setMasterData,setMasterDatas, addSpokenLanguagesList, removeSpokenLanguagesList,setEmergencyContactRelationship} = personalInfoSlice.actions;
export default personalInfoSlice.reducer;
