import WorkHistory from "./workHistory";
import Education from "./education";
import React from "react";
import {useDispatch, useSelector} from "react-redux";
import {updateNoResume, updateResumeFile, uploadResume} from "./action";
import {resumeBuilderSelector} from "./selector";
import useConfiguration from "../../../hooks/useConfiguration";
import useSessionStorage from "../../../hooks/useSessionStorage";

export default (props) => {
    const {errors, educationErrors, workHistoryErrors } = props;
    const dispatch = useDispatch();
    const { data } = useSelector(resumeBuilderSelector);
    const config = useConfiguration();
    const storage = useSessionStorage();
    const login = storage.getItem("login");
    const noResume = data && data.noResume;
    const resumeFile = data && data.resumeFile && data.resumeFile.name;
    const clickNoResume = () => {
        dispatch(updateNoResume(!data.noResume));
    }
    const fileChange = (e) => {
        const file = e.target.files[0];
        const resumeFile = {
            name: file.name,
        }
        dispatch(updateResumeFile(resumeFile));
        dispatch(uploadResume(config.baseUrl, login.candidateId, file));
    }
    return (
        <>
            <div className="row">
                <div className="col-lg-6 col-md-12">
                    <div className="form-group">
                    <form>
                   {/* { 
                    noResume ?  
                    <input className="form-control" type="file" disabled="true" onChange={fileChange} />

                       :
                       <input className="form-control" type="file" onChange={fileChange} />

                    }*/}
                    <div>Resume File*</div>
<input className="form-control" type="file" disabled={noResume} onChange={fileChange} />

                    {errors['resume'] && errors['resume'].map(error => (<div className="invalid-input">{error}</div>))}
                    </form>
                    </div>
                </div>
                <div className="col-lg-6 col-md-12" htmlFor="chknoresume">
                    <div className="form-group">
                    <div>&nbsp;</div>
                        <div className="custom-control custom-checkbox">

                            <input className="custom-control-input custom-control-input-danger" type="checkbox" id="chknoresume"  onClick={clickNoResume} checked={noResume}   />
                            <label htmlFor="chknoresume" className="custom-control-label"><h4><b>No Resume</b>
                            </h4></label>
                        </div>
                    </div>
                </div>
            </div>
            { noResume && <WorkHistory errors={workHistoryErrors}/> }
            { noResume && <Education errors={educationErrors}/> }
        </>
    )
}
